import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-2906 Application for escort vehicle driver certificate; driving record; proof of completion of escort vehicle driver training; fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2906.md","filePath":"vacode/46.2-2906.md"}'),a={name:"vacode/46.2-2906.md"},o=t('<h1 id="§-46-2-2906-application-for-escort-vehicle-driver-certificate-driving-record-proof-of-completion-of-escort-vehicle-driver-training-fee" tabindex="-1">§ 46.2-2906 Application for escort vehicle driver certificate; driving record; proof of completion of escort vehicle driver training; fee. <a class="header-anchor" href="#§-46-2-2906-application-for-escort-vehicle-driver-certificate-driving-record-proof-of-completion-of-escort-vehicle-driver-training-fee" aria-label="Permalink to &quot;§ 46.2-2906 Application for escort vehicle driver certificate; driving record; proof of completion of escort vehicle driver training; fee.&quot;">​</a></h1><p>A. Every application for an escort vehicle driver certificate shall be made on a form prescribed by the Department, and the applicant shall write his usual signature in ink in the space provided on the form. A person who applies for an escort vehicle driver certificate must meet the following requirements:</p><ol><li><p>Be at least 18 years of age;</p></li><li><p>Hold a valid Virginia driver&#39;s license or a valid driver&#39;s license for another state;</p></li><li><p>Authorize the Department to review his driving record;</p></li><li><p>Present satisfactory proof of successful completion of an eight-hour escort vehicle driver certification training course, as required by § <a href="http://law.lis.virginia.gov/vacode/46.2-2904/" target="_blank" rel="noreferrer">46.2-2904</a>;</p></li><li><p>Pass the escort vehicle driver certification knowledge test as required by § <a href="http://law.lis.virginia.gov/vacode/46.2-2905/" target="_blank" rel="noreferrer">46.2-2905</a> with a score of 80 percent or higher; and</p></li><li><p>Pay the appropriate fee for certificate issuance.</p></li></ol><p>B. Every application shall state the applicant&#39;s full legal name; year, month, and date of birth; social security number; sex; and residence address. The applicant shall also answer any questions on the application form, or otherwise propounded, and provide any other information as required by the Department incidental to the application.</p><p>C. The Commissioner shall require that each application include a certification statement, to be signed by the applicant under penalty of perjury, certifying that the information presented on the application is true and correct. If the applicant fails or refuses to sign the certification statement, the Department shall not issue the applicant an escort vehicle driver certificate.</p><p>Any applicant who knowingly makes a false certification or supplies false or fictitious evidence shall be punished as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-348/" target="_blank" rel="noreferrer">46.2-348</a>.</p><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0312" target="_blank" rel="noreferrer">312</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0258" target="_blank" rel="noreferrer">258</a>.</p>',7),n=[o];function c(l,s,p,f,h,d){return i(),r("div",null,n)}const u=e(a,[["render",c]]);export{g as __pageData,u as default};
