import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-2907 Nonresident; extensions of reciprocal privileges.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-2907.md","filePath":"vacode/46.2-2907.md"}'),a={name:"vacode/46.2-2907.md"},n=r('<h1 id="§-46-2-2907-nonresident-extensions-of-reciprocal-privileges" tabindex="-1">§ 46.2-2907 Nonresident; extensions of reciprocal privileges. <a class="header-anchor" href="#§-46-2-2907-nonresident-extensions-of-reciprocal-privileges" aria-label="Permalink to &quot;§ 46.2-2907 Nonresident; extensions of reciprocal privileges.&quot;">​</a></h1><p>A nonresident age 18 years or older who has been duly licensed as a driver under a law regulating the licensure of drivers in his home state and who has in his immediate possession a valid driver&#39;s license and a valid escort vehicle driver certificate issued to him in his home state, where such state&#39;s escort vehicle driver certification program has been determined to be substantially similar to the Commonwealth&#39;s and to which the Commonwealth has extended reciprocity, shall be permitted without a Virginia license or a Virginia escort vehicle driver certificate to escort a permitted vehicle or vehicles on the highways of the Commonwealth. Such nonresident shall be exempt from the escort vehicle driver certification eligibility, training, and testing requirements of this chapter.</p><p>If such nonresident desires to also hold a Virginia escort vehicle driver certificate, in addition to the valid certificate issued to him by his home state, he must then meet all of the Virginia escort vehicle driver certification eligibility, training, and testing requirements of this chapter.</p><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0312" target="_blank" rel="noreferrer">312</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0258" target="_blank" rel="noreferrer">258</a>.</p>',4),o=[n];function s(c,l,h,d,p,f){return i(),t("div",null,o)}const m=e(a,[["render",s]]);export{v as __pageData,m as default};
