import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-341.14:2 Requirements for third party examiners.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-341.14:2.md","filePath":"vacode/46.2-341.14:2.md"}'),a={name:"vacode/46.2-341.14:2.md"},n=t('<h1 id="§-46-2-341-14-2-requirements-for-third-party-examiners" tabindex="-1">§ 46.2-341.14:2 Requirements for third party examiners. <a class="header-anchor" href="#§-46-2-341-14-2-requirements-for-third-party-examiners" aria-label="Permalink to &quot;§ 46.2-341.14:2 Requirements for third party examiners.&quot;">​</a></h1><p>A. Third party examiners may be certified to conduct skills tests on behalf of only one third party tester at any given time. If a third party examiner leaves the employ of a third party tester, he must be recertified in order to conduct skills tests on behalf of a new third party tester.</p><p>B. To qualify for certification as a third party examiner, an individual must:</p><ol><li><p>Make application to the Department as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-341.14:3/" target="_blank" rel="noreferrer">46.2-341.14:3</a> and pass the required nationwide criminal background check;</p></li><li><p>Be an employee of the third party tester;</p></li><li><p>Possess a valid Virginia commercial driver&#39;s license with the classification and endorsements required for operation of the class and type of commercial motor vehicle used in skills tests conducted by the examiner;</p></li><li><p>Satisfactorily complete any third party examiner training course required by the Department;</p></li><li><p>Within three years prior to application, have had no driver&#39;s license suspensions, revocations, or disqualifications;</p></li><li><p>At the time of application, have no more than six demerit points on his driving record and not be on probation under the Virginia Driver Improvement Program;</p></li><li><p>Within three years prior to application, have had no conviction for any offense listed in § <a href="http://law.lis.virginia.gov/vacode/46.2-341.18/" target="_blank" rel="noreferrer">46.2-341.18</a> or <a href="http://law.lis.virginia.gov/vacode/46.2-341.19/" target="_blank" rel="noreferrer">46.2-341.19</a>, whether or not such offense was committed in a commercial motor vehicle;</p></li><li><p>If the examiner is employed by a school board, be certified by the Virginia Department of Education as a school bus training instructor;</p></li><li><p>Conduct skills tests on behalf of the third party tester in accordance with this article and in accordance with current instructions provided by the Department; and</p></li><li><p>Successfully complete a training course and examination every four years to maintain the commercial driver&#39;s license test examiner certification.</p></li></ol><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0165" target="_blank" rel="noreferrer">165</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0582" target="_blank" rel="noreferrer">582</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0077" target="_blank" rel="noreferrer">77</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>.</p>',5),o=[n];function s(l,c,p,d,h,f){return i(),r("div",null,o)}const v=e(a,[["render",s]]);export{u as __pageData,v as default};
