import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse(`{"title":"§ 46.2-341.15 Commercial driver's license and commercial learner's permit document.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-341.15.md","filePath":"vacode/46.2-341.15.md"}`),l={name:"vacode/46.2-341.15.md"},t=a('<h1 id="§-46-2-341-15-commercial-driver-s-license-and-commercial-learner-s-permit-document" tabindex="-1">§ 46.2-341.15 Commercial driver&#39;s license and commercial learner&#39;s permit document. <a class="header-anchor" href="#§-46-2-341-15-commercial-driver-s-license-and-commercial-learner-s-permit-document" aria-label="Permalink to &quot;§ 46.2-341.15 Commercial driver&#39;s license and commercial learner&#39;s permit document.&quot;">​</a></h1><p>A. The commercial driver&#39;s license issued by the Department shall be identified as a Virginia commercial driver&#39;s license and shall include at least the following:</p><ol><li><p>Full name, a Virginia address, and signature of the licensee;</p></li><li><p>A photograph of the licensee;</p></li><li><p>A physical description of the licensee, including sex and height;</p></li><li><p>The licensee&#39;s date of birth and license number that shall be assigned by the Department to the licensee and shall not be the same as the licensee&#39;s Social Security number;</p></li><li><p>A designation of the class and type of commercial motor vehicle or vehicles which the licensee is authorized to drive, together with any restrictions; and</p></li><li><p>The date of license issuance and expiration.</p></li></ol><p>B. The commercial learner&#39;s permit shall be identified as such but shall in all other respects conform to subsection A of this section. A commercial learner&#39;s permit shall also contain a statement that the permit is invalid unless accompanied by the underlying driver&#39;s license.</p><p>C. A nondomiciled commercial driver&#39;s license or a nondomiciled commercial learner&#39;s permit shall contain the word &quot;nondomiciled&quot; on the face of the document.</p><p>1989, c. 705, § 46.1-372.15; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0190" target="_blank" rel="noreferrer">190</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0165" target="_blank" rel="noreferrer">165</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0582" target="_blank" rel="noreferrer">582</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0258" target="_blank" rel="noreferrer">258</a>.</p>',6),n=[t];function c(o,s,d,m,h,p){return i(),r("div",null,n)}const u=e(l,[["render",c]]);export{f as __pageData,u as default};
