import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-341.20:6 Prohibition on requiring use of handheld mobile telephone or texting; motor carrier penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-341.20:6.md","filePath":"vacode/46.2-341.20:6.md"}'),a={name:"vacode/46.2-341.20:6.md"},r=o('<h1 id="§-46-2-341-20-6-prohibition-on-requiring-use-of-handheld-mobile-telephone-or-texting-motor-carrier-penalty" tabindex="-1">§ 46.2-341.20:6 Prohibition on requiring use of handheld mobile telephone or texting; motor carrier penalty. <a class="header-anchor" href="#§-46-2-341-20-6-prohibition-on-requiring-use-of-handheld-mobile-telephone-or-texting-motor-carrier-penalty" aria-label="Permalink to &quot;§ 46.2-341.20:6 Prohibition on requiring use of handheld mobile telephone or texting; motor carrier penalty.&quot;">​</a></h1><p>No motor carrier shall allow or require its drivers to use a handheld mobile telephone or to text while driving a commercial motor vehicle. Motor carriers violating this section are subject to a civil penalty not to exceed $11,000. Civil penalties collected under this section shall be deposited into the Highway Maintenance and Operating Fund established pursuant to § <a href="/vacode/33.2-1530/">33.2-1530</a>. Pursuant to 49 C.F.R. § 386.81, the determination of the actual civil penalties assessed is based on consideration of information available at the time the claim is made concerning the nature and gravity of the violation and, with respect to the violator, the degree of culpability, history of prior offenses, ability to pay, effect on ability to continue to do business, and such other matters as justice and public safety may require. &quot;Driving,&quot; &quot;mobile telephone,&quot; &quot;texting,&quot; and &quot;use a handheld mobile telephone&quot; have the same meanings as assigned to them in § <a href="/vacode/46.2-341.20:5/">46.2-341.20:5</a>.</p><p>2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0077" target="_blank" rel="noreferrer">77</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',3),n=[r];function l(s,h,c,d,p,u){return t(),i("div",null,n)}const m=e(a,[["render",l]]);export{f as __pageData,m as default};
