import{_ as e,o as i,c as o,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-341.21 Driving while disqualified; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-341.21.md","filePath":"vacode/46.2-341.21.md"}'),r={name:"vacode/46.2-341.21.md"},n=a('<h1 id="§-46-2-341-21-driving-while-disqualified-penalties" tabindex="-1">§ 46.2-341.21 Driving while disqualified; penalties. <a class="header-anchor" href="#§-46-2-341-21-driving-while-disqualified-penalties" aria-label="Permalink to &quot;§ 46.2-341.21 Driving while disqualified; penalties.&quot;">​</a></h1><p>No person whose privilege to drive a commercial motor vehicle has been suspended or revoked or who has been disqualified from operating a commercial motor vehicle or who has been ordered out of service, and who has been given notice of, or reasonably should know of the suspension, revocation, disqualification, or out-of-service order shall operate a commercial motor vehicle anywhere in the Commonwealth until the period of such suspension, revocation, disqualification, or out-of-service order has terminated, nor shall any person operate on any highway any vehicle that has been declared out of service until such time as the out-of-service declaration has been lifted.</p><p>Any person who violates this section shall, for the first offense, be guilty of a Class 2 misdemeanor, and for the second or any subsequent offense, be guilty of a Class 1 misdemeanor; however, if the offense is the violation of an out-of-service order, the minimum mandatory fine shall be $2,500 for any person so convicted of a first offense and $5,000 for a person convicted of a second or subsequent offense. Upon receipt of a record of a violation of this section, the Commissioner shall impose an additional disqualification in accordance with the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/46.2-341.18/" target="_blank" rel="noreferrer">46.2-341.18</a> and <a href="http://law.lis.virginia.gov/vacode/46.2-341.18:01/" target="_blank" rel="noreferrer">46.2-341.18:01</a>.</p><p>1989, c. 705, § 46.1-372.20; 1990, c. 218; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0145" target="_blank" rel="noreferrer">145</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0151" target="_blank" rel="noreferrer">151</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0513" target="_blank" rel="noreferrer">513</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0190" target="_blank" rel="noreferrer">190</a>.</p>',4),t=[n];function s(l,c,f,h,d,p){return i(),o("div",null,t)}const g=e(r,[["render",s]]);export{u as __pageData,g as default};
