import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-341.26:9 Assurance of breath test validity; use of breath tests as evidence.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-341.26:9.md","filePath":"vacode/46.2-341.26:9.md"}'),r={name:"vacode/46.2-341.26:9.md"},s=i('<h1 id="§-46-2-341-26-9-assurance-of-breath-test-validity-use-of-breath-tests-as-evidence" tabindex="-1">§ 46.2-341.26:9 Assurance of breath test validity; use of breath tests as evidence. <a class="header-anchor" href="#§-46-2-341-26-9-assurance-of-breath-test-validity-use-of-breath-tests-as-evidence" aria-label="Permalink to &quot;§ 46.2-341.26:9 Assurance of breath test validity; use of breath tests as evidence.&quot;">​</a></h1><p>To be capable of being considered valid in a prosecution under § <a href="http://law.lis.virginia.gov/vacode/46.2-341.24/" target="_blank" rel="noreferrer">46.2-341.24</a> or <a href="http://law.lis.virginia.gov/vacode/46.2-341.31/" target="_blank" rel="noreferrer">46.2-341.31</a>, chemical analysis of a person&#39;s breath shall be performed by an individual possessing a valid license to conduct such tests, with the type of equipment and in accordance with methods approved by the Department.</p><p>Any individual conducting a breath test under the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-341.26:2/" target="_blank" rel="noreferrer">46.2-341.26:2</a> shall issue a certificate which includes the name of the suspect, the date and time the sample was taken from the suspect, the alcohol content of the sample, and the identity of the person who examined the sample. The certificate will also indicate that the test was conducted in accordance with the Department&#39;s specifications.</p><p>The certificate of analysis, when attested by the authorized individual conducting the breath test on equipment maintained by the Department, shall be admissible in any court as evidence of the facts therein stated and of the results of such analysis (i) in any criminal proceeding, provided that the requirements of subsection A of § <a href="http://law.lis.virginia.gov/vacode/19.2-187.1/" target="_blank" rel="noreferrer">19.2-187.1</a> have been satisfied and the accused has not objected to the admission of the certificate pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/19.2-187.1/" target="_blank" rel="noreferrer">19.2-187.1</a>, or (ii) in any civil proceeding. Any such certificate of analysis purporting to be signed by a person authorized by the Department shall be admissible in evidence without proof of seal or signature of the person whose name is signed to it.</p><p>A copy of such certificate shall be promptly delivered to the suspect. Any person qualified to conduct a breath test as provided by this section may administer the breath test or analyze the results thereof.</p><p>1992, c. 830; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>; 2009, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0004" target="_blank" rel="noreferrer">4</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0623" target="_blank" rel="noreferrer">623</a>.</p>',6),n=[s];function o(c,h,l,d,f,p){return t(),a("div",null,n)}const b=e(r,[["render",o]]);export{v as __pageData,b as default};
