import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse(`{"title":"§ 46.2-341.7 Commercial driver's license required; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-341.7.md","filePath":"vacode/46.2-341.7.md"}`),o={name:"vacode/46.2-341.7.md"},l=a('<h1 id="§-46-2-341-7-commercial-driver-s-license-required-penalty" tabindex="-1">§ 46.2-341.7 Commercial driver&#39;s license required; penalty. <a class="header-anchor" href="#§-46-2-341-7-commercial-driver-s-license-required-penalty" aria-label="Permalink to &quot;§ 46.2-341.7 Commercial driver&#39;s license required; penalty.&quot;">​</a></h1><p>A. No person shall drive a commercial motor vehicle in the Commonwealth unless he has been issued a commercial driver&#39;s license or commercial learner&#39;s permit and unless such license or permit authorizes the operation of the type and class of vehicle so driven, and unless such license or permit is valid.</p><p>B. Every driver of a commercial motor vehicle, while driving such vehicle in the Commonwealth, shall have in his immediate possession the commercial driver&#39;s license or commercial learner&#39;s permit authorizing the operation of such vehicle and shall make it available to any law-enforcement officer upon request. Failure to comply with this subsection shall be punishable as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-104/" target="_blank" rel="noreferrer">46.2-104</a>.</p><p>C. No person shall drive a commercial vehicle in Virginia in violation of any of the restrictions or limitations stated on his commercial driver&#39;s license or commercial learner&#39;s permit. A violation of the subsection shall constitute a Class 2 misdemeanor.</p><p>1989, c. 705, § 46.1-372.7; 1993, c. 70; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0165" target="_blank" rel="noreferrer">165</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0582" target="_blank" rel="noreferrer">582</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0258" target="_blank" rel="noreferrer">258</a>.</p>',5),t=[l];function s(n,c,m,h,d,p){return i(),r("div",null,t)}const u=e(o,[["render",s]]);export{_ as __pageData,u as default};
