import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 46.2-389 Required revocation for one year upon conviction or finding of guilty of certain offenses; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-389.md","filePath":"vacode/46.2-389.md"}'),a={name:"vacode/46.2-389.md"},t=i('<h1 id="§-46-2-389-required-revocation-for-one-year-upon-conviction-or-finding-of-guilty-of-certain-offenses-exceptions" tabindex="-1">§ 46.2-389 Required revocation for one year upon conviction or finding of guilty of certain offenses; exceptions. <a class="header-anchor" href="#§-46-2-389-required-revocation-for-one-year-upon-conviction-or-finding-of-guilty-of-certain-offenses-exceptions" aria-label="Permalink to &quot;§ 46.2-389 Required revocation for one year upon conviction or finding of guilty of certain offenses; exceptions.&quot;">​</a></h1><p>A. The Commissioner shall forthwith revoke, and not thereafter reissue for a period of time specified in subsection B, except as provided in § <a href="http://law.lis.virginia.gov/vacode/18.2-271/" target="_blank" rel="noreferrer">18.2-271</a> or § <a href="http://law.lis.virginia.gov/vacode/18.2-271.1/" target="_blank" rel="noreferrer">18.2-271.1</a>, the driver&#39;s license of any resident or nonresident on receiving a record of his conviction or a record of his having been found guilty in the case of a juvenile of any of the following crimes, committed in violation of a state law or a valid county, city, or town ordinance or law of the United States, or a law of any other state, substantially paralleling and substantially conforming to a like state law and to all changes and amendments of it:</p><ol><li><p>Voluntary or involuntary manslaughter resulting from the driving of a motor vehicle;</p></li><li><p>Violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-266/" target="_blank" rel="noreferrer">18.2-266</a> or § <a href="http://law.lis.virginia.gov/vacode/18.2-272/" target="_blank" rel="noreferrer">18.2-272</a>, or subsection A of § <a href="http://law.lis.virginia.gov/vacode/46.2-341.24/" target="_blank" rel="noreferrer">46.2-341.24</a> or violation of a substantially similar local ordinance;</p></li><li><p>Perjury or the making of a false affidavit to the Department under this chapter or any other law of the Commonwealth requiring the registration of motor vehicles or regulating their operation on the highways;</p></li><li><p>The making of a false statement to the Department on any application for a driver&#39;s license;</p></li><li><p>Any crime punishable as a felony under the motor vehicle laws of the Commonwealth or any other felony in the commission of which a motor vehicle is used;</p></li><li><p>Failure to stop and disclose his identity at the scene of the accident, on the part of a driver of a motor vehicle involved in an accident resulting in the death of or injury to another person; or</p></li><li><p>Violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-36.1/" target="_blank" rel="noreferrer">18.2-36.1</a> or § <a href="http://law.lis.virginia.gov/vacode/18.2-51.4/" target="_blank" rel="noreferrer">18.2-51.4</a>.</p></li></ol><p>B. Upon conviction of an offense set forth in subsection A, the person&#39;s driver&#39;s license shall be revoked for one year; however, for a violation of subdivision A 1 or A 7, the driver&#39;s license shall be revoked as provided in subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-391/" target="_blank" rel="noreferrer">46.2-391</a>. However, in no such event shall the Commissioner reinstate the driver&#39;s license of any person convicted of a violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-266/" target="_blank" rel="noreferrer">18.2-266</a>, or of a substantially similar valid local ordinance or law of another jurisdiction, until receipt of notification that such person has successfully completed an alcohol safety action program if such person was required by a court to do so unless the requirement for completion of the program has been waived by the court for good cause shown.</p><p>Code 1950, § 46-416; 1958, cc. 496, 541, § 46.1-417; 1960, c. 364; 1966, c. 238; 1974, c. 453; 1976, cc. 612, 691; 1982, c. 301; 1984, c. 780; 1988, c. 860; 1989, cc. 705, 727; 1990, c. 949; 1992, cc. 109, 891; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0486" target="_blank" rel="noreferrer">486</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0691" target="_blank" rel="noreferrer">691</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0945" target="_blank" rel="noreferrer">945</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0987" target="_blank" rel="noreferrer">987</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0956" target="_blank" rel="noreferrer">956</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0959" target="_blank" rel="noreferrer">959</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0982" target="_blank" rel="noreferrer">982</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0985" target="_blank" rel="noreferrer">985</a>.</p>',5),n=[t];function l(s,c,f,h,g,v){return o(),r("div",null,n)}const u=e(a,[["render",l]]);export{d as __pageData,u as default};
