import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-393 Suspension of license on conviction of certain reckless offenses; restricted licenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-393.md","filePath":"vacode/46.2-393.md"}'),i={name:"vacode/46.2-393.md"},s=t('<h1 id="§-46-2-393-suspension-of-license-on-conviction-of-certain-reckless-offenses-restricted-licenses" tabindex="-1">§ 46.2-393 Suspension of license on conviction of certain reckless offenses; restricted licenses. <a class="header-anchor" href="#§-46-2-393-suspension-of-license-on-conviction-of-certain-reckless-offenses-restricted-licenses" aria-label="Permalink to &quot;§ 46.2-393 Suspension of license on conviction of certain reckless offenses; restricted licenses.&quot;">​</a></h1><p>A. When any person is convicted of reckless driving as provided in §§ <a href="http://law.lis.virginia.gov/vacode/46.2-853/" target="_blank" rel="noreferrer">46.2-853</a> through <a href="http://law.lis.virginia.gov/vacode/46.2-864/" target="_blank" rel="noreferrer">46.2-864</a>, in addition to any penalties provided by law, the driver&#39;s license of the person may be suspended by the court for a period of not less than 60 days nor more than six months. In case of conviction the court shall order the surrender of the license to the court where it shall be disposed of in accordance with the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-398/" target="_blank" rel="noreferrer">46.2-398</a>. If the person so convicted has not obtained a license required by this chapter or is a nonresident, the court shall direct in the judgment of conviction that the person shall not drive any motor vehicle in the Commonwealth for a period of not less than 60 days nor more than six months.</p><p>B. The court may, in its discretion and for good cause shown, provide that such person be issued a restricted permit to operate a motor vehicle during the period of suspension for any of the purposes set forth in subsection E of § <a href="http://law.lis.virginia.gov/vacode/18.2-271.1/" target="_blank" rel="noreferrer">18.2-271.1</a>. The court shall forward to the Commissioner a copy of its order entered pursuant to this section, which shall specifically enumerate the restrictions imposed and contain such information regarding the person to whom such a permit is issued as is reasonably necessary to identify such person. The court shall also provide a copy of its order to the person who may operate a motor vehicle on the order until receipt from the Commissioner of a restricted license. A copy of such order and, after receipt thereof, the restricted license shall be carried at all times while operating a motor vehicle. Any person who operates a motor vehicle in violation of any restrictions imposed pursuant to this section shall be punished as provided in subsection C of § <a href="http://law.lis.virginia.gov/vacode/46.2-301/" target="_blank" rel="noreferrer">46.2-301</a>. No restricted license issued pursuant to this section shall permit any person to operate a commercial motor vehicle as defined in the Virginia Commercial Driver&#39;s License Act (§ <a href="http://law.lis.virginia.gov/vacode/46.2-341.1/" target="_blank" rel="noreferrer">46.2-341.1</a> et seq.).</p><p>Code 1950, § 46-209.1; 1954, c. 401; 1958, c. 541, § 46.1-423; 1960, c. 200; 1966, c. 694; 1974, c. 453; 1984, c. 780; 1989, c. 727; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0152" target="_blank" rel="noreferrer">152</a>.</p>',4),n=[s];function a(c,l,h,d,f,p){return o(),r("div",null,n)}const m=e(i,[["render",a]]);export{u as __pageData,m as default};
