import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-402 When Commissioner may suspend or revoke license for not more than one year after hearing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-402.md","filePath":"vacode/46.2-402.md"}'),t={name:"vacode/46.2-402.md"},n=i('<h1 id="§-46-2-402-when-commissioner-may-suspend-or-revoke-license-for-not-more-than-one-year-after-hearing" tabindex="-1">§ 46.2-402 When Commissioner may suspend or revoke license for not more than one year after hearing. <a class="header-anchor" href="#§-46-2-402-when-commissioner-may-suspend-or-revoke-license-for-not-more-than-one-year-after-hearing" aria-label="Permalink to &quot;§ 46.2-402 When Commissioner may suspend or revoke license for not more than one year after hearing.&quot;">​</a></h1><p>A. The Commissioner may, after due hearing, after giving not less than five days&#39; written notice by registered letter to the most recent address of the driver on file at the Department, suspend or revoke for not more than one year and not thereafter reissue during the period of suspension or revocation the Virginia driver&#39;s license issued to any person whenever it is satisfactorily proved at the hearing conducted by the Commissioner or other personnel of the Department designated by him, that the licensee under charges:</p><ol><li><p>Has, by reckless or unlawful operation of a motor vehicle, caused or contributed to an accident resulting in death or injury to any other person or in serious property damage,</p></li><li><p>Is incompetent to drive a motor vehicle,</p></li><li><p>Suffers from mental or physical infirmities or disabilities rendering it unsafe for him to drive a motor vehicle on the highways,</p></li><li><p>Is habitually a reckless or negligent driver of a motor vehicle, or</p></li><li><p>Has committed a serious violation of the motor vehicle laws of this Commonwealth.</p></li></ol><p>B. The Commissioner, in determining the propriety of suspending or revoking a license as provided in this section, may take into consideration facts and conditions antedating the issuance of the current license.</p><p>Code 1950, § 46-420; 1958, c. 541, § 46.1-430; 1984, c. 780; 1989, c. 727; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0943" target="_blank" rel="noreferrer">943</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0994" target="_blank" rel="noreferrer">994</a>.</p>',5),a=[n];function s(h,c,l,d,m,p){return o(),r("div",null,a)}const g=e(t,[["render",s]]);export{u as __pageData,g as default};
