import{_ as s,o as t,c as i,z as e,a as o}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 46.2-410.2 License suspension or revocation by Commissioner; offenses under the laws of other jurisdictions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-410.2.md","filePath":"vacode/46.2-410.2.md"}'),n={name:"vacode/46.2-410.2.md"},r=e("h1",{id:"§-46-2-410-2-license-suspension-or-revocation-by-commissioner-offenses-under-the-laws-of-other-jurisdictions",tabindex:"-1"},[o("§ 46.2-410.2 License suspension or revocation by Commissioner; offenses under the laws of other jurisdictions. "),e("a",{class:"header-anchor",href:"#§-46-2-410-2-license-suspension-or-revocation-by-commissioner-offenses-under-the-laws-of-other-jurisdictions","aria-label":'Permalink to "§ 46.2-410.2 License suspension or revocation by Commissioner; offenses under the laws of other jurisdictions."'},"​")],-1),a=e("p",null,"Notwithstanding any other provision of this chapter, the Commissioner shall not administratively revoke or suspend the driver's license of any person on the basis of receiving a record of such person's conviction for any offense under the laws of another jurisdiction that would otherwise require the Commissioner to revoke or suspend such person's driver's license unless such offense is substantially similar to an offense under the laws of the Commonwealth or a county, city, or town ordinance. Whenever the Commissioner is required to determine whether the law of another jurisdiction is substantially similar to the laws of the Commonwealth, or a county, city, or town ordinance, such determination shall be based only on the text of the other jurisdiction's law without reference to the particular circumstances of any conviction under such other jurisdiction's laws. However, if the Commissioner cannot reasonably determine from the text of the other jurisdiction's law whether such law is substantially similar to the laws of the Commonwealth, or a county, city, or town ordinance, the Commissioner may, if available, review a certified copy of the final order of the person's conviction in order to make such determination.",-1),c=e("p",null,[o("2017, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0776",target:"_blank",rel:"noreferrer"},"776"),o(".")],-1),h=[r,a,c];function l(d,f,u,m,p,w){return t(),i("div",null,h)}const _=s(n,[["render",l]]);export{y as __pageData,_ as default};
