import{_ as t,o,c as i,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 46.2-426 Custody and application of cash or securities deposited; limitation of actions; assignment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-426.md","filePath":"vacode/46.2-426.md"}'),s={name:"vacode/46.2-426.md"},n=e("h1",{id:"§-46-2-426-custody-and-application-of-cash-or-securities-deposited-limitation-of-actions-assignment",tabindex:"-1"},[a("§ 46.2-426 Custody and application of cash or securities deposited; limitation of actions; assignment. "),e("a",{class:"header-anchor",href:"#§-46-2-426-custody-and-application-of-cash-or-securities-deposited-limitation-of-actions-assignment","aria-label":'Permalink to "§ 46.2-426 Custody and application of cash or securities deposited; limitation of actions; assignment."'},"​")],-1),r=e("p",null,"Cash or securities furnished in compliance with the requirements of this chapter shall be placed by the Commissioner in the custody of the State Treasurer and shall be applicable only to the payment of any judgment against the depositor for damages arising out of the accident in question in an action at law in a court in the Commonwealth begun not later than one year after the date of the accident. The cash or securities may be assigned by the depositor for the benefit of the person or persons damaged or injured in the accident as the result of which the cash or securities were filed or deposited without the damaged or injured person being required to institute legal proceedings. The Commissioner shall accept the assignment if, in his opinion, the rights of any other person or persons shall not be prejudiced thereby.",-1),c=e("p",null,"Code 1950, § 46-441; 1958, c. 541, § 46.1-454; 1989, c. 727.",-1),d=[n,r,c];function h(l,p,u,m,f,_){return o(),i("div",null,d)}const b=t(s,[["render",h]]);export{y as __pageData,b as default};
