import{_ as o,o as i,c as e,O as s}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-469 Commission not required to disclose reasons for action; liability of Commission for act or omission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-469.md","filePath":"vacode/46.2-469.md"}'),a={name:"vacode/46.2-469.md"},n=s('<h1 id="§-46-2-469-commission-not-required-to-disclose-reasons-for-action-liability-of-commission-for-act-or-omission" tabindex="-1">§ 46.2-469 Commission not required to disclose reasons for action; liability of Commission for act or omission. <a class="header-anchor" href="#§-46-2-469-commission-not-required-to-disclose-reasons-for-action-liability-of-commission-for-act-or-omission" aria-label="Permalink to &quot;§ 46.2-469 Commission not required to disclose reasons for action; liability of Commission for act or omission.&quot;">​</a></h1><p>A. The Commission shall not be required to disclose to any person, including the applicant or policyholder, its reasons for:</p><ol><li><p>Refusing to assign an application;</p></li><li><p>Approving the rejection of an application by an insurance carrier;</p></li><li><p>Approving the cancellation of a policy of motor vehicle liability, physical damage, and medical payments insurance by an insurance carrier; or</p></li><li><p>Refusing to approve the renewal or the reassignment of an expiring policy.</p></li></ol><p>B. The Commission or anyone acting for it shall not be held liable for any act or omission in connection with the administration of the duties imposed upon it by the provisions of this chapter, except upon proof of actual malfeasance.</p><p>Code 1950, § 46-490; 1958, c. 541, § 46.1-501; 1972, c. 842; 1989, c. 727.</p>',5),t=[n];function r(c,l,p,m,d,f){return i(),e("div",null,t)}const u=o(a,[["render",r]]);export{_ as __pageData,u as default};
