import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const y=JSON.parse(`{"title":"§ 46.2-472 Coverage of owner's policy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-472.md","filePath":"vacode/46.2-472.md"}`),i={name:"vacode/46.2-472.md"},n=r('<h1 id="§-46-2-472-coverage-of-owner-s-policy" tabindex="-1">§ 46.2-472 Coverage of owner&#39;s policy. <a class="header-anchor" href="#§-46-2-472-coverage-of-owner-s-policy" aria-label="Permalink to &quot;§ 46.2-472 Coverage of owner&#39;s policy.&quot;">​</a></h1><p>A. For all policies effective on or after January 1, 2022, but prior to January 1, 2025, every motor vehicle owner&#39;s policy shall:</p><ol><li><p>Designate by explicit description or by appropriate reference, all motor vehicles with respect to which coverage is intended to be granted.</p></li><li><p>Insure as insured the person named and any other person using or responsible for the use of the motor vehicle or motor vehicles with the permission of the named insured.</p></li><li><p>Insure the insured or other person against loss from any liability imposed by law for damages, including damages for care and loss of services, because of bodily injury to or death of any person, and injury to or destruction of property caused by accident and arising out of the ownership, use, or operation of such motor vehicle or motor vehicles within the Commonwealth, any other state in the United States, or Canada, subject to a limit exclusive of interest and costs, with respect to each motor vehicle, of $30,000 because of bodily injury to or death of one person in any one accident and, subject to the limit for one person, to a limit of $60,000 because of bodily injury to or death of two or more persons in any one accident, and to a limit of $20,000 because of injury to or destruction of property of others in any one accident.</p></li></ol><p>B. For all policies effective on or after January 1, 2025, every motor vehicle owner&#39;s policy shall:</p><ol><li><p>Designate, by explicit description or appropriate reference, all motor vehicles with respect to which coverage is intended to be granted.</p></li><li><p>Insure as insured the person named and any other person using or responsible for the use of the motor vehicle or vehicles with the permission of the named insured.</p></li><li><p>Insure the insured or other person against loss from any liability imposed by law for damages, including damages for care and loss of services, because of bodily injury to or death of any person, and injury to or destruction of property caused by an accident and arising out of the ownership, use, or operation of such motor vehicle or vehicles within the Commonwealth, any other state in the United States, or Canada, subject to a limit exclusive of interest and costs, with respect to each motor vehicle, of $50,000 because of bodily injury to or death of one person in any one accident and, subject to the limit for one person, to a limit of $100,000 because of bodily injury to or death of two or more persons in any one accident, and to a limit of $25,000 because of injury to or destruction of property of others in any one accident.</p></li></ol><p>Code 1950, § 46-492; 1954, c. 378; 1958, cc. 501, 541, § 46.1-504; 1968, c. 685; 1972, c. 433; 1975, c. 382; 1978, c. 550; 1989, cc. 621, 727; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>.</p>',6),a=[n];function s(c,l,d,p,h,f){return o(),t("div",null,a)}const m=e(i,[["render",s]]);export{y as __pageData,m as default};
