import{_ as n,o as i,c as a,z as e,a as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-494 Safe driving point credit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-494.md","filePath":"vacode/46.2-494.md"}'),t={name:"vacode/46.2-494.md"},r=e("h1",{id:"§-46-2-494-safe-driving-point-credit",tabindex:"-1"},[o("§ 46.2-494 Safe driving point credit. "),e("a",{class:"header-anchor",href:"#§-46-2-494-safe-driving-point-credit","aria-label":'Permalink to "§ 46.2-494 Safe driving point credit."'},"​")],-1),s=e("p",null,"Every resident or nonresident person holding a valid Virginia driver's license whose driving record does not contain any suspension, revocation, conviction, or finding of not innocent in the case of a juvenile, of a traffic violation, during any calendar year shall be awarded one safe driving point. One safe driving point shall be awarded for each calendar year of safe driving, but no person shall be permitted to accumulate more than five safe driving points. The Commissioner shall apply these points to offset an equivalent number of demerit points, if any, to the chronologically earliest offense conviction, or finding of not innocent in the case of a juvenile, for which demerit points have been assigned and are valid. If subsequent to awarding a safe driving point to any person, the Department receives a conviction, or finding of not innocent in the case of a juvenile, for an offense which occurred during the period for which a safe driving point was awarded for and which requires the Department to assess demerit points, the safe driving point shall be invalidated.",-1),d=e("p",null,"1974, c. 453, § 46.1-514.8; 1978, c. 44; 1989, c. 727.",-1),c=[r,s,d];function f(l,h,p,v,g,u){return i(),a("div",null,c)}const b=n(t,[["render",f]]);export{_ as __pageData,b as default};
