import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-495 Advisory letters.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-495.md","filePath":"vacode/46.2-495.md"}'),r={name:"vacode/46.2-495.md"},a=o('<h1 id="§-46-2-495-advisory-letters" tabindex="-1">§ 46.2-495 Advisory letters. <a class="header-anchor" href="#§-46-2-495-advisory-letters" aria-label="Permalink to &quot;§ 46.2-495 Advisory letters.&quot;">​</a></h1><p>Whenever the driving record of any person who is eighteen years old or older shows an accumulation of at least eight demerit points based on convictions for traffic offenses committed within a period of twelve consecutive months, or at least twelve demerit points based on convictions for traffic offenses committed within a period of twenty-four consecutive months, respectively, the Commissioner may mail, by first-class mail, to the last known address of the person an advisory letter listing his convictions and the demerit points assigned thereto, including his safe driving points, if any, and furnish any other information deemed appropriate and applicable to the rehabilitation of the person, for the purpose of preventing subsequent traffic offenses.</p><p>The Department&#39;s failure to mail, or the citizen&#39;s nonreceipt of the advisory letter shall not be grounds for waiving any other provision of this article.</p><p>1974, c. 453, § 46.1-514.9; 1984, c. 673; 1989, c. 727; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0672" target="_blank" rel="noreferrer">672</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0124" target="_blank" rel="noreferrer">124</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0792" target="_blank" rel="noreferrer">792</a>.</p>',4),n=[a];function s(c,l,d,f,h,p){return t(),i("div",null,n)}const m=e(r,[["render",s]]);export{_ as __pageData,m as default};
