import{_ as e,o as r,c as o,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-500 Driver control period.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-500.md","filePath":"vacode/46.2-500.md"}'),t={name:"vacode/46.2-500.md"},i=a('<h1 id="§-46-2-500-driver-control-period" tabindex="-1">§ 46.2-500 Driver control period. <a class="header-anchor" href="#§-46-2-500-driver-control-period" aria-label="Permalink to &quot;§ 46.2-500 Driver control period.&quot;">​</a></h1><p>Whenever an individual is placed on probation pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/46.2-498/" target="_blank" rel="noreferrer">46.2-498</a>, <a href="http://law.lis.virginia.gov/vacode/46.2-499/" target="_blank" rel="noreferrer">46.2-499</a> or § <a href="http://law.lis.virginia.gov/vacode/46.2-506/" target="_blank" rel="noreferrer">46.2-506</a>, the Commissioner shall also place the person on driver control status for a period of eighteen months following the termination of the probationary period. If the individual commits any violation during the driver control period for which points are assessed, the Commissioner shall again place the individual on probation for a period of six months and on driver control status for an additional period of eighteen months following the probationary period.</p><p>1984, c. 673, § 46.1-514.13:1; 1989, c. 727; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0672" target="_blank" rel="noreferrer">672</a>.</p>',3),n=[i];function l(s,d,c,p,h,_){return r(),o("div",null,n)}const g=e(t,[["render",l]]);export{v as __pageData,g as default};
