import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-501 Notice to attend driver improvement clinic.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-501.md","filePath":"vacode/46.2-501.md"}'),o={name:"vacode/46.2-501.md"},r=n('<h1 id="§-46-2-501-notice-to-attend-driver-improvement-clinic" tabindex="-1">§ 46.2-501 Notice to attend driver improvement clinic. <a class="header-anchor" href="#§-46-2-501-notice-to-attend-driver-improvement-clinic" aria-label="Permalink to &quot;§ 46.2-501 Notice to attend driver improvement clinic.&quot;">​</a></h1><p>A. Any notice to attend a driver improvement clinic shall contain:</p><ol><li><p>Information on how to schedule a driver improvement clinic.</p></li><li><p>The purpose of the driver improvement clinic, including the consequences of not attending the clinic program.</p></li><li><p>An explanation of the terms of the probationary licensing period.</p></li><li><p>A requirement stating that the clinic must be satisfactorily completed within ninety days from the date of the notice. The Commissioner may for good cause shown, and provided the person provides the Commissioner with satisfactory evidence documenting the need and soonest date of return, extend the time limit otherwise provided for attending such a clinic when the person directed to attend a driver improvement clinic is (i) attending an institution of higher education outside Virginia, and attendance is to coincide with a break in the school year of such institution of higher education, provided that jurisdiction does not offer an approved driver improvement clinic or (ii) in the military or is a military dependent and is stationed outside the United States or outside the Commonwealth in a jurisdiction that does not offer an approved driver improvement clinic.</p></li></ol><p>B. The notice directing any person to attend a driver improvement clinic shall be forwarded by certified mail to the last known address of the person, as shown on the records of the Department.</p><p>1974, c. 453, § 46.1-514.14; 1989, c. 727; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0672" target="_blank" rel="noreferrer">672</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0385" target="_blank" rel="noreferrer">385</a>.</p>',5),a=[r];function c(d,s,l,p,h,m){return t(),i("div",null,a)}const _=e(o,[["render",c]]);export{f as __pageData,_ as default};
