import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-600 Owner to secure registration and certificate of title or certificate of ownership.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-600.md","filePath":"vacode/46.2-600.md"}'),a={name:"vacode/46.2-600.md"},o=i('<h1 id="§-46-2-600-owner-to-secure-registration-and-certificate-of-title-or-certificate-of-ownership" tabindex="-1">§ 46.2-600 Owner to secure registration and certificate of title or certificate of ownership. <a class="header-anchor" href="#§-46-2-600-owner-to-secure-registration-and-certificate-of-title-or-certificate-of-ownership" aria-label="Permalink to &quot;§ 46.2-600 Owner to secure registration and certificate of title or certificate of ownership.&quot;">​</a></h1><p>Except as otherwise provided, for the purposes of this chapter, a moped shall be deemed a motor vehicle.</p><p>Except as otherwise provided in this chapter every person who owns a motor vehicle, trailer or semitrailer, or his authorized attorney-in-fact, shall, before it is operated on any highway in the Commonwealth, register with the Department and obtain from the Department the registration card and certificate of title for the vehicle. Individuals applying for registration shall provide the Department with the residence address of the owner of the vehicle being registered. A business applying for registration shall provide the Department with the street address of the owner or lessee of the vehicle being registered.</p><p>At the option of the applicant for registration, the address shown on the title and registration card may be either a post office box or the business or residence address of the applicant.</p><p>Unless he has previously applied for registration and a certificate of title or he is exempted under §§ <a href="http://law.lis.virginia.gov/vacode/46.2-619/" target="_blank" rel="noreferrer">46.2-619</a>, <a href="http://law.lis.virginia.gov/vacode/46.2-626.1/" target="_blank" rel="noreferrer">46.2-626.1</a>, <a href="http://law.lis.virginia.gov/vacode/46.2-631/" target="_blank" rel="noreferrer">46.2-631</a>, and <a href="http://law.lis.virginia.gov/vacode/46.2-1206/" target="_blank" rel="noreferrer">46.2-1206</a>, every person residing in the Commonwealth who owns a motor vehicle, trailer, or semitrailer, or his duly authorized attorney-in-fact, shall, within 30 days of the purchase or transfer, apply to the Department for a certificate of ownership.</p><p>Nothing in this chapter shall be construed to require titling or registration in the Commonwealth of any farm tractor or special construction and forestry equipment, as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-100/" target="_blank" rel="noreferrer">46.2-100</a>.</p><p>Notwithstanding the foregoing provisions of this section, provided such vehicle is registered and titled elsewhere in the United States, nothing in this chapter shall be construed to require titling or registration in the Commonwealth of any vehicle located in the Commonwealth if that vehicle is registered to a non-Virginia resident active duty military service member, activated reserve or national guard member, mobilized reserve or national guard member living in the Commonwealth, or person who is serving a full-time church service or proselyting mission of not more than 36 months and who is not gainfully employed.</p><p>Code 1950, § 46-42; 1958, c. 541, § 46.1-41; 1972, c. 301; 1978, c. 402; 1980, c. 469; 1986, c. 228; 1988, c. 363; 1989, c. 727; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0297" target="_blank" rel="noreferrer">297</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0934" target="_blank" rel="noreferrer">934</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0135" target="_blank" rel="noreferrer">135</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0244" target="_blank" rel="noreferrer">244</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0367" target="_blank" rel="noreferrer">367</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0428" target="_blank" rel="noreferrer">428</a>.</p>',8),n=[o];function s(l,h,c,f,p,d){return r(),t("div",null,n)}const m=e(a,[["render",s]]);export{v as __pageData,m as default};
