import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-602.1 Titling and registration of replica vehicles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-602.1.md","filePath":"vacode/46.2-602.1.md"}'),r={name:"vacode/46.2-602.1.md"},l=t('<h1 id="§-46-2-602-1-titling-and-registration-of-replica-vehicles" tabindex="-1">§ 46.2-602.1 Titling and registration of replica vehicles. <a class="header-anchor" href="#§-46-2-602-1-titling-and-registration-of-replica-vehicles" aria-label="Permalink to &quot;§ 46.2-602.1 Titling and registration of replica vehicles.&quot;">​</a></h1><p>Notwithstanding any other provision of this chapter, the model year of vehicles constructed or assembled by multiple manufacturers or assemblers shall be the model year of which the vehicle is a replica. No vehicle titled under this section shall be driven more than 5,000 miles per year as shown by the vehicle&#39;s odometer. No vehicle titled under this section shall be automatically eligible for antique motor vehicle license plates provided for in § <a href="http://law.lis.virginia.gov/vacode/46.2-730/" target="_blank" rel="noreferrer">46.2-730</a>.</p><p>Any vehicle registered under this section shall be subject to vehicle safety inspections as provided for in Article 21 (§ <a href="http://law.lis.virginia.gov/vacode/46.2-1157/" target="_blank" rel="noreferrer">46.2-1157</a> et seq.) of Chapter 10 and emissions inspections as provided for in Article 22 (§ <a href="http://law.lis.virginia.gov/vacode/46.2-1176/" target="_blank" rel="noreferrer">46.2-1176</a> et seq.) of Chapter 10. Such vehicles shall meet such safety and emission requirements as established for the model year of which the vehicle is a replica.</p><p>The Department shall assign each such vehicle a new vehicle identification number, line-make, and model year, if required.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0325" target="_blank" rel="noreferrer">325</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0393" target="_blank" rel="noreferrer">393</a>.</p>',5),o=[l];function s(n,c,h,d,p,f){return i(),a("div",null,o)}const g=e(r,[["render",s]]);export{_ as __pageData,g as default};
