import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-606 Notice of change of address.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-606.md","filePath":"vacode/46.2-606.md"}'),r={name:"vacode/46.2-606.md"},n=o('<h1 id="§-46-2-606-notice-of-change-of-address" tabindex="-1">§ 46.2-606 Notice of change of address. <a class="header-anchor" href="#§-46-2-606-notice-of-change-of-address" aria-label="Permalink to &quot;§ 46.2-606 Notice of change of address.&quot;">​</a></h1><p>A. Whenever any person who has applied for or obtained the registration or title to a vehicle moves from the address shown in his application, registration card or certificate of title, he shall notify the Department of his change of address within 30 days.</p><p>B. The Department may contract with the United States Postal Service or an authorized agent to use the National Change of Address System for the purpose of obtaining current address information for a person whose name appears in customer records maintained by the Department. If the Department receives information from the National Change of Address System indicating that a person whose name appears in a Department record has submitted a permanent change of address to the Postal Service, the Department may then update its records with the mailing address obtained from the National Change of Address System.</p><p>C. Anyone failing to comply with this section may be charged a fee of $5, to be used to cover the Department&#39;s expenses. Notwithstanding the foregoing provision of this subsection, no fee shall be imposed on any person whose address is obtained from the National Change of Address System.</p><p>1974, c. 347, § 46.1-52.1; 1989, c. 727; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0943" target="_blank" rel="noreferrer">943</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0994" target="_blank" rel="noreferrer">994</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0025" target="_blank" rel="noreferrer">25</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>.</p>',5),i=[n];function s(h,d,c,f,p,l){return t(),a("div",null,i)}const _=e(r,[["render",s]]);export{m as __pageData,_ as default};
