import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-613 Infractions relating to registration, licensing, and certificates of title; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-613.md","filePath":"vacode/46.2-613.md"}'),a={name:"vacode/46.2-613.md"},n=r('<h1 id="§-46-2-613-infractions-relating-to-registration-licensing-and-certificates-of-title-penalties" tabindex="-1">§ 46.2-613 Infractions relating to registration, licensing, and certificates of title; penalties. <a class="header-anchor" href="#§-46-2-613-infractions-relating-to-registration-licensing-and-certificates-of-title-penalties" aria-label="Permalink to &quot;§ 46.2-613 Infractions relating to registration, licensing, and certificates of title; penalties.&quot;">​</a></h1><p>A. No person shall:</p><ol><li><p>Operate, park, or permit the operation or parking of a motor vehicle, trailer, or semitrailer owned, leased, or otherwise controlled by him on a highway unless (i) it is registered, (ii) a certificate of title therefor has been issued, and (iii) it has displayed on it the license plate or plates and decal or decals, if any, assigned to it by the Department for the current registration period, subject to the exemptions mentioned in Article 5 (§ <a href="/vacode/46.2-655/">46.2-655</a> et seq.) and Article 6 (§ <a href="/vacode/46.2-662/">46.2-662</a> et seq.). The provisions of this subdivision shall apply to the registration, licensing, and titling of mopeds on or after July 1, 2014.</p></li><li><p>Possess or use any registration card, license plate, or decal to which he is not entitled or knowingly permit the use of any registration card, license plate, or decal by anyone not entitled to it.</p></li><li><p>Willfully and intentionally violate the limitations imposed under §§ <a href="/vacode/46.2-665/">46.2-665</a>, <a href="/vacode/46.2-666/">46.2-666</a>, and <a href="/vacode/46.2-670/">46.2-670</a> while operating an unregistered vehicle pursuant to the agricultural and horticultural exemptions allowed under those sections. A first violation of this subdivision shall constitute a traffic infraction punishable by a fine of not more than $250, and a second or subsequent violation of this subdivision shall constitute a traffic infraction punishable by a fine of $250.</p></li></ol><p>B. For any summons issued for a violation of this section, the court may, in its discretion, dismiss the summons, where proof of compliance with this section is provided to the court on or before the court date.</p><p>Code 1950, § 46-63; 1950, p. 251; 1958, c. 541, § 46.1-64; 1960, c. 79; 1972, c. 609; 1974, c. 400; 1975, c. 124; 1979, c. 620; 1989, c. 727; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0212" target="_blank" rel="noreferrer">212</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0093" target="_blank" rel="noreferrer">93</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0444" target="_blank" rel="noreferrer">444</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0472" target="_blank" rel="noreferrer">472</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0204" target="_blank" rel="noreferrer">204</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0425" target="_blank" rel="noreferrer">425</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0071" target="_blank" rel="noreferrer">71</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>.</p>',5),o=[n];function l(s,c,f,g,h,p){return i(),t("div",null,o)}const v=e(a,[["render",l]]);export{u as __pageData,v as default};
