import{_ as i,o,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-622 Issuance of certificate of title in names of joint owners.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-622.md","filePath":"vacode/46.2-622.md"}'),s={name:"vacode/46.2-622.md"},r=e("h1",{id:"§-46-2-622-issuance-of-certificate-of-title-in-names-of-joint-owners",tabindex:"-1"},[t("§ 46.2-622 Issuance of certificate of title in names of joint owners. "),e("a",{class:"header-anchor",href:"#§-46-2-622-issuance-of-certificate-of-title-in-names-of-joint-owners","aria-label":'Permalink to "§ 46.2-622 Issuance of certificate of title in names of joint owners."'},"​")],-1),a=e("p",null,'When the Department receives an application for a certificate of title for a motor vehicle, trailer, or semitrailer, to be issued in the names of two natural persons, jointly with right of survivorship, the Department shall issue to its owners a certificate of title accordingly. Any certificate issued in the name of two persons may contain an expression such as "or the survivor of them," which shall be deemed sufficient to create joint ownership during the lives of the two owners, and individual ownership in the survivor. A certificate issued in the names of two persons, with their names separated only by "or," shall create joint ownership during the lives of the owners, and individual ownership in the survivor of them.',-1),c=e("p",null,"Nothing herein shall (i) prohibit the issuance of a certificate of title in the names of two or more persons as owners in common which shall be sufficient evidence of ownership of undivided interests in the vehicle; (ii) grant immunity from enforcement of any liability of any person owning the vehicle, as one of two joint owners, to the extent of his interest in the vehicle, during the lives of its owners; (iii) permit the issuance of a certificate of title in the names of two persons as tenants by the entireties; or (iv) be used by one of the joint owners as a defense to the secured party's enforcement of a security interest in the vehicle that was granted by one or both of the joint owners of the vehicle on the same date or prior to the issuance of the certificate of title.",-1),h=e("p",null,[t("1968, c. 188, § 46.1-68.1; 1983, c. 586; 1989, c. 727; 2002, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0432",target:"_blank",rel:"noreferrer"},"432"),t(".")],-1),f=[r,a,c,h];function l(d,m,u,p,w,v){return o(),n("div",null,f)}const g=i(s,[["render",l]]);export{b as __pageData,g as default};
