import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-624 Information required on vehicles damaged by water.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-624.md","filePath":"vacode/46.2-624.md"}'),o={name:"vacode/46.2-624.md"},n=r('<h1 id="§-46-2-624-information-required-on-vehicles-damaged-by-water" tabindex="-1">§ 46.2-624 Information required on vehicles damaged by water. <a class="header-anchor" href="#§-46-2-624-information-required-on-vehicles-damaged-by-water" aria-label="Permalink to &quot;§ 46.2-624 Information required on vehicles damaged by water.&quot;">​</a></h1><p>A. When a vehicle has been damaged by water to such an extent that the insurance company insuring it has paid a claim of $3,500 or more because of this water damage, the insurance company shall report the payment of such claim to the Department.</p><p>B. Upon receipt of information from an insurance company pursuant to subsection A, the Commissioner shall issue a new certificate of title and place an appropriate indicator upon such certificate in order to convey that information to the new owner of the motor vehicle.</p><p>1966, c. 550, § 46.1-64.1; 1989, c. 727; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0652" target="_blank" rel="noreferrer">652</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0678" target="_blank" rel="noreferrer">678</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0072" target="_blank" rel="noreferrer">72</a>.</p>',4),i=[n];function c(s,l,h,d,p,m){return a(),t("div",null,i)}const u=e(o,[["render",c]]);export{_ as __pageData,u as default};
