import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-644.1 Titling of all-terrain vehicles and off-road motorcycles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-644.1.md","filePath":"vacode/46.2-644.1.md"}'),o={name:"vacode/46.2-644.1.md"},i=a('<h1 id="§-46-2-644-1-titling-of-all-terrain-vehicles-and-off-road-motorcycles" tabindex="-1">§ 46.2-644.1 Titling of all-terrain vehicles and off-road motorcycles. <a class="header-anchor" href="#§-46-2-644-1-titling-of-all-terrain-vehicles-and-off-road-motorcycles" aria-label="Permalink to &quot;§ 46.2-644.1 Titling of all-terrain vehicles and off-road motorcycles.&quot;">​</a></h1><p>A. Every owner, except a dealer licensed under § <a href="http://law.lis.virginia.gov/vacode/46.2-1508/" target="_blank" rel="noreferrer">46.2-1508</a>, of any all-terrain vehicle or off-road motorcycle powered by a gasoline or diesel engine displacing more than 50 cubic centimeters and purchased as new on or after July 1, 2006, shall apply to the Department for a certificate of title in the name of the owner before the all-terrain vehicle or off-road motorcycle is operated anywhere in the Commonwealth.</p><p>B. Any owner of an all-terrain vehicle or off-road motorcycle not required to be titled under this section and not titled elsewhere may apply to the Department for a certificate of title. The Department shall issue the certificate upon reasonable evidence of ownership, such as a buyer&#39;s order or other document satisfactory to the Department.</p><p>C. Except as otherwise provided in this title, all-terrain vehicles and off-road motorcycles shall comply with the titling requirements of motor vehicles pursuant to Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/46.2-616/" target="_blank" rel="noreferrer">46.2-616</a> et seq.).</p><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0896" target="_blank" rel="noreferrer">896</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',5),l=[i];function n(c,s,f,d,h,p){return t(),r("div",null,l)}const v=e(o,[["render",n]]);export{m as __pageData,v as default};
