import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-646.1 Deactivation and reactivation of registration; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-646.1.md","filePath":"vacode/46.2-646.1.md"}'),r={name:"vacode/46.2-646.1.md"},o=i('<h1 id="§-46-2-646-1-deactivation-and-reactivation-of-registration-fees" tabindex="-1">§ 46.2-646.1 Deactivation and reactivation of registration; fees. <a class="header-anchor" href="#§-46-2-646-1-deactivation-and-reactivation-of-registration-fees" aria-label="Permalink to &quot;§ 46.2-646.1 Deactivation and reactivation of registration; fees.&quot;">​</a></h1><p>A. The owner of a motor vehicle that has been registered in the Commonwealth may apply to the Commissioner to deactivate the registration of such vehicle. The owner of a motor vehicle who has voluntarily deactivated the vehicle&#39;s registration pursuant to this section shall not be required, with respect to such vehicle, to carry bodily injury liability insurance or property damage insurance, or to pay the uninsured motor vehicle fee as provided under § <a href="http://law.lis.virginia.gov/vacode/46.2-706/" target="_blank" rel="noreferrer">46.2-706</a>.</p><p>It shall be unlawful to operate any motor vehicle whose registration has been deactivated on any highway in the Commonwealth.</p><p>B. Any person having a motor vehicle for which registration has been deactivated under subsection A may apply to the Commissioner to reactivate the registration of such vehicle. Every applicant for reactivation of registration shall furnish the Commissioner with such evidence as is required under § <a href="http://law.lis.virginia.gov/vacode/46.2-649/" target="_blank" rel="noreferrer">46.2-649</a> and shall either (i) execute and furnish to the Commissioner his certificate that the motor vehicle for which registration is to be reactivated is an insured motor vehicle as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-705/" target="_blank" rel="noreferrer">46.2-705</a>, or that the Commissioner has issued to its owner, in accordance with § <a href="http://law.lis.virginia.gov/vacode/46.2-368/" target="_blank" rel="noreferrer">46.2-368</a>, a certificate of self-insurance applicable to the vehicle or (ii) pay the uninsured motor vehicle fee required by § <a href="http://law.lis.virginia.gov/vacode/46.2-706/" target="_blank" rel="noreferrer">46.2-706</a>, which shall be disposed of as provided by § <a href="http://law.lis.virginia.gov/vacode/46.2-710/" target="_blank" rel="noreferrer">46.2-710</a>. The fee to be paid to the Department for the reactivation of a motor vehicle&#39;s registration shall be $10 unless the vehicle&#39;s registration has expired or the vehicle is registered under the International Registration Plan.</p><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0673" target="_blank" rel="noreferrer">673</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0789" target="_blank" rel="noreferrer">789</a>.</p>',5),n=[o];function s(h,c,l,d,v,f){return t(),a("div",null,n)}const u=e(r,[["render",s]]);export{g as __pageData,u as default};
