import{_ as t,o as e,c as r,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 46.2-649.1 Registration of tow trucks; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-649.1.md","filePath":"vacode/46.2-649.1.md"}'),o={name:"vacode/46.2-649.1.md"},s=a('<h1 id="§-46-2-649-1-registration-of-tow-trucks-fees" tabindex="-1">§ 46.2-649.1 Registration of tow trucks; fees. <a class="header-anchor" href="#§-46-2-649-1-registration-of-tow-trucks-fees" aria-label="Permalink to &quot;§ 46.2-649.1 Registration of tow trucks; fees.&quot;">​</a></h1><p>A. No tow truck registered under this section shall be subject to registration under the international registration plan or subject to any other state registration requirements under this chapter. Registration under this section shall not prohibit the use of &quot;rollbacks&quot; to transport storage sheds, similar structures, or other cargoes.</p><p>B. Vehicles registered under this section shall be subject to the following annual fees, based upon their manufacturer&#39;s gross vehicle weight ratings:</p><table><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td>a</td><td>less than 15,000 pounds</td><td>$100</td></tr><tr><td>b</td><td>15,000 to 22,999 pounds</td><td>$200</td></tr><tr><td>c</td><td>23,000 to 29,499 pounds</td><td>$300</td></tr><tr><td>d</td><td>more than 29,499 pounds</td><td>$400</td></tr></tbody></table><p>C. No vehicle shall be registered under this section unless there is in force as to such vehicle at the time of its registration commercial liability insurance coverage for those classes of insurance defined in §§ <a href="https://law.lis.virginia.gov/vacode/38.2-117/" target="_blank" rel="noreferrer">38.2-117</a> and <a href="https://law.lis.virginia.gov/vacode/38.2-118/" target="_blank" rel="noreferrer">38.2-118</a> in the amount of at least $750,000.</p><p>1993, c. 120; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0874" target="_blank" rel="noreferrer">874</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0891" target="_blank" rel="noreferrer">891</a>.</p>',6),i=[s];function n(d,c,l,h,u,f){return e(),r("div",null,i)}const _=t(o,[["render",n]]);export{p as __pageData,_ as default};
