import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-649 Certain vehicles required to show evidence of payment of taxes and of registration or exemption from registration with Department of Motor Vehicles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-649.md","filePath":"vacode/46.2-649.md"}'),o={name:"vacode/46.2-649.md"},a=i('<h1 id="§-46-2-649-certain-vehicles-required-to-show-evidence-of-payment-of-taxes-and-of-registration-or-exemption-from-registration-with-department-of-motor-vehicles" tabindex="-1">§ 46.2-649 Certain vehicles required to show evidence of payment of taxes and of registration or exemption from registration with Department of Motor Vehicles. <a class="header-anchor" href="#§-46-2-649-certain-vehicles-required-to-show-evidence-of-payment-of-taxes-and-of-registration-or-exemption-from-registration-with-department-of-motor-vehicles" aria-label="Permalink to &quot;§ 46.2-649 Certain vehicles required to show evidence of payment of taxes and of registration or exemption from registration with Department of Motor Vehicles.&quot;">​</a></h1><p>A. Before the Commissioner registers or reregisters any motor vehicle, trailer, or semitrailer under § <a href="http://law.lis.virginia.gov/vacode/46.2-697/" target="_blank" rel="noreferrer">46.2-697</a>, <a href="http://law.lis.virginia.gov/vacode/46.2-698/" target="_blank" rel="noreferrer">46.2-698</a>, <a href="http://law.lis.virginia.gov/vacode/46.2-700/" target="_blank" rel="noreferrer">46.2-700</a>, or <a href="http://law.lis.virginia.gov/vacode/46.2-703/" target="_blank" rel="noreferrer">46.2-703</a>, the applicant shall furnish evidence satisfactory to the Commissioner that all state, local, and federal taxes levied on that motor vehicle, trailer, or semitrailer have been paid and that the motor vehicle, trailer, or semitrailer either (i) is registered with the Department as required by law, or (ii) is not required so to register.</p><p>B. The Commissioner, in consultation with local commissioners of the revenue and directors of finance, and with appropriate federal officials, shall provide for the kinds of evidence required to satisfy the provisions of subsection A.</p><p>C. The provisions of this section shall not apply to (i) pickup trucks, (ii) panel trucks, or (iii) trucks having a registered gross weight less than 33,000 pounds.</p><p>D. The State Corporation Commission may notify the Department that a motor carrier (i) has not filed an annual report as required by § <a href="http://law.lis.virginia.gov/vacode/58.1-2654/" target="_blank" rel="noreferrer">58.1-2654</a> or (ii) has not paid taxes due as required by the State Corporation Commission. Upon receiving the notice, the Department shall not register or reregister motor vehicles, trailers, or semitrailers owned by the motor carrier until such requirements have been met.</p><p>1983, c. 515, § 46.1-153.1; 1989, c. 727; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0047" target="_blank" rel="noreferrer">47</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0226" target="_blank" rel="noreferrer">226</a>.</p>',6),n=[a];function s(l,h,c,f,p,d){return r(),t("div",null,n)}const g=e(o,[["render",s]]);export{v as __pageData,g as default};
