import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-652 Temporary registration or permit for oversize vehicles; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-652.md","filePath":"vacode/46.2-652.md"}'),o={name:"vacode/46.2-652.md"},a=i('<h1 id="§-46-2-652-temporary-registration-or-permit-for-oversize-vehicles-fees" tabindex="-1">§ 46.2-652 Temporary registration or permit for oversize vehicles; fees. <a class="header-anchor" href="#§-46-2-652-temporary-registration-or-permit-for-oversize-vehicles-fees" aria-label="Permalink to &quot;§ 46.2-652 Temporary registration or permit for oversize vehicles; fees.&quot;">​</a></h1><p>The Commissioner may grant a temporary registration or permit for the operation of a vehicle or equipment that cannot be licensed because the vehicle, excluding any load thereon, exceeds statutory size limits on the highways in the Commonwealth from one point to another within the Commonwealth, or from the Commonwealth to a point or points outside the Commonwealth, or from outside the Commonwealth to a point or points within the Commonwealth. Any temporary registration or permit issued under this section shall show the registration or permit number, the date of issue, the date of expiration, the vehicle to which it refers, and the route to be traveled or other restrictions and shall be carried in the vehicle.</p><p>For a single-trip temporary registration or permit issued under this section, the applicant shall pay a fee of 10 cents ($0.10) per mile for every mile to be traveled, in addition to any administrative fee required by the Department. In lieu of a single-trip permit, an annual multi-trip permit may be issued for a fee of $40, in addition to any administrative fee required by the Department.</p><p>For any vehicle that is both overweight and oversize, the permit fees under § <a href="http://law.lis.virginia.gov/vacode/46.2-652.1/" target="_blank" rel="noreferrer">46.2-652.1</a> shall apply.</p><p>Code 1950, § 46-44; 1958, c. 541, § 46.1-43; 1962, c. 535; 1989, c. 727; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0314" target="_blank" rel="noreferrer">314</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0443" target="_blank" rel="noreferrer">443</a>.</p>',5),n=[a];function s(h,l,p,m,c,d){return t(),r("div",null,n)}const g=e(o,[["render",s]]);export{v as __pageData,g as default};
