import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-662 Temporary exemption for new resident operating vehicle registered in another state or country.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-662.md","filePath":"vacode/46.2-662.md"}'),a={name:"vacode/46.2-662.md"},n=o('<h1 id="§-46-2-662-temporary-exemption-for-new-resident-operating-vehicle-registered-in-another-state-or-country" tabindex="-1">§ 46.2-662 Temporary exemption for new resident operating vehicle registered in another state or country. <a class="header-anchor" href="#§-46-2-662-temporary-exemption-for-new-resident-operating-vehicle-registered-in-another-state-or-country" aria-label="Permalink to &quot;§ 46.2-662 Temporary exemption for new resident operating vehicle registered in another state or country.&quot;">​</a></h1><p>A. A resident owner of any passenger car, pickup or panel truck, moped, autocycle, or motorcycle, other than those provided for in § <a href="http://law.lis.virginia.gov/vacode/46.2-652/" target="_blank" rel="noreferrer">46.2-652</a>, that has been duly registered for the current calendar year in another state or country and that at all times when operated in the Commonwealth displays the license plate or plates issued for the vehicle in the other state or country, may operate or permit the operation of the passenger car, pickup or panel truck, moped, autocycle, or motorcycle within or partly within the Commonwealth for the first 30 days of his residency in the Commonwealth without registering the passenger car, pickup or panel truck, moped, autocycle, or motorcycle or paying any fees to the Commonwealth.</p><p>B. In addition to any penalty authorized under this title, any locality may adopt an ordinance imposing a penalty of up to $250 upon the resident owner of any motor vehicle that, following the end of the 30-day period provided in subsection A, is required to be registered in Virginia but has not been so registered. The locality may impose the penalty upon the resident owner annually for as long as the motor vehicle remains unregistered in Virginia. The ordinance shall set forth a reasonable method for assessing and collecting the penalty, whether by civil, criminal, or administrative process, and shall identify the employees or agents of the locality who are to execute such assessment and collection.</p><p>1976, c. 17, § 46.1-41.1; 1980, c. 53; 1989, c. 727; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0347" target="_blank" rel="noreferrer">347</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0131" target="_blank" rel="noreferrer">131</a>.</p>',4),i=[n];function s(l,c,h,p,d,g){return t(),r("div",null,i)}const y=e(a,[["render",s]]);export{m as __pageData,y as default};
