import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 46.2-665 (Effective July 1, 2023) Vehicles used for agricultural or horticultural purposes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-665.md","filePath":"vacode/46.2-665.md"}'),t={name:"vacode/46.2-665.md"},o=i('<h1 id="§-46-2-665-effective-july-1-2023-vehicles-used-for-agricultural-or-horticultural-purposes" tabindex="-1">§ 46.2-665 (Effective July 1, 2023) Vehicles used for agricultural or horticultural purposes. <a class="header-anchor" href="#§-46-2-665-effective-july-1-2023-vehicles-used-for-agricultural-or-horticultural-purposes" aria-label="Permalink to &quot;§ 46.2-665 (Effective July 1, 2023) Vehicles used for agricultural or horticultural purposes.&quot;">​</a></h1><p>A. No person shall be required to obtain the registration certificate, license plates, or decals for or pay a registration fee for any motor vehicle, trailer, or semitrailer used exclusively for agricultural or horticultural purposes on lands owned or leased by the vehicle&#39;s owner.</p><p>B. This exemption shall only apply to (i) pickup or panel trucks, (ii) sport utility vehicles, (iii) vehicles having a gross vehicle weight rating greater than 7,500 pounds, and (iv) trailers and semitrailers that are not operated on or over any public highway in the Commonwealth for any purpose other than:</p><ol><li><p>Crossing a highway;</p></li><li><p>Operating along a highway for a distance of no more than 75 miles from one part of the owner&#39;s land to another, irrespective of whether the tracts adjoin;</p></li><li><p>Taking the vehicle or attached fixtures to and from a repair shop for repairs;</p></li><li><p>Taking another vehicle exempt from registration under any provision of §§ <a href="/vacode/46.2-664/">46.2-664</a> through <a href="/vacode/46.2-668/">46.2-668</a> or <a href="/vacode/46.2-672/">46.2-672</a>, or any part or subcomponent of such a vehicle, to or from a repair shop for repairs, including return trips;</p></li><li><p>Operating along a highway to and from a refuse disposal facility for the purpose of disposing of trash and garbage generated on a farm;</p></li><li><p>Operating along a highway for a distance of no more than 75 miles for the purpose of obtaining supplies for agricultural or horticultural purposes, seeds, fertilizers, chemicals, or animal feed and returning; or</p></li><li><p>Transporting the vehicle&#39;s owner between his residence and the lands being used for agricultural or horticultural purposes.</p></li></ol><p>C. The owner or lessee of a vehicle, trailer, or semitrailer claiming the exemption provided pursuant to this section shall be required to obtain a permanent farm use placard pursuant to § <a href="https://law.lis.virginia.gov/vacode/46.2-684.2/" target="_blank" rel="noreferrer">46.2-684.2</a>.</p><p>Code 1950, § 46-45; 1950, p. 693; 1952, c. 498; 1956, cc. 292, 568; 1958, c. 541, § 46.1-45; 1962, cc. 214, 535; 1964, c. 611; 1966, c. 654; 1968, c. 46; 1970, c. 192; 1972, c. 609; 1973, c. 495; 1978, c. 307; 1988, cc. 76, 568; 1989, c. 727; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0253" target="_blank" rel="noreferrer">253</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0318" target="_blank" rel="noreferrer">318</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0327" target="_blank" rel="noreferrer">327</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0293" target="_blank" rel="noreferrer">293</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0174" target="_blank" rel="noreferrer">174</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0776" target="_blank" rel="noreferrer">776</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0142" target="_blank" rel="noreferrer">142</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0538" target="_blank" rel="noreferrer">538</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0781" target="_blank" rel="noreferrer">781</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0051" target="_blank" rel="noreferrer">51</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0052" target="_blank" rel="noreferrer">52</a>.</p>',6),l=[o];function n(s,c,p,h,g,f){return r(),a("div",null,l)}const v=e(t,[["render",n]]);export{d as __pageData,v as default};
