import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-678 Forklift trucks.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-678.md","filePath":"vacode/46.2-678.md"}'),a={name:"vacode/46.2-678.md"},i=o('<h1 id="§-46-2-678-forklift-trucks" tabindex="-1">§ 46.2-678 Forklift trucks. <a class="header-anchor" href="#§-46-2-678-forklift-trucks" aria-label="Permalink to &quot;§ 46.2-678 Forklift trucks.&quot;">​</a></h1><p>A. No person shall be required to obtain the registration certificate, license plates, or decals for or pay a registration fee for any forklift truck provided it is:</p><ol><li><p>Operated by a person holding a valid Virginia driver&#39;s license;</p></li><li><p>Operated along or across highways only in traveling from one plant, factory, or job site to another by the most direct route;</p></li><li><p>Not carrying or transporting any object or person, other than the driver;</p></li><li><p>Displaying a slow-moving vehicle emblem in conformity with § <a href="http://law.lis.virginia.gov/vacode/46.2-1081/" target="_blank" rel="noreferrer">46.2-1081</a>;</p></li><li><p>In compliance with requirements of the federal Occupational Safety and Health Administration;</p></li><li><p>Not operated on or along any limited access highway; and</p></li><li><p>Not operated for a distance of more than ten miles.</p></li></ol><p>B. For the purposes of this section, &quot;forklift truck&quot; means a self-propelled machine used for hoisting and transporting heavy objects by means of steel fingers inserted under the load.</p><p>1973, c. 194, § 46.1-45.2; 1980, c. 37; 1986, c. 220; 1987, cc. 151, 342, 388; 1989, c. 727; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0142" target="_blank" rel="noreferrer">142</a>.</p>',5),n=[i];function l(s,c,p,d,f,h){return t(),r("div",null,n)}const m=e(a,[["render",l]]);export{g as __pageData,m as default};
