import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-692.2 Fee for exchange of license plates.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-692.2.md","filePath":"vacode/46.2-692.2.md"}'),o={name:"vacode/46.2-692.2.md"},s=r('<h1 id="§-46-2-692-2-fee-for-exchange-of-license-plates" tabindex="-1">§ 46.2-692.2 Fee for exchange of license plates. <a class="header-anchor" href="#§-46-2-692-2-fee-for-exchange-of-license-plates" aria-label="Permalink to &quot;§ 46.2-692.2 Fee for exchange of license plates.&quot;">​</a></h1><p>The fee for the exchange of license plates shall be the greater of the total of any statutory fees required for the requested license plates, as calculated under the provisions of subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-694/" target="_blank" rel="noreferrer">46.2-694</a>, or $10.</p><p>As used in this section, an &quot;exchange of license plates&quot; means a transaction that occurs within the registration period of a vehicle in which the vehicle owner voluntarily returns the license plates assigned to the vehicle and requests for the same vehicle new license plates with a different design or alphanumeric combination or both.</p><p>A request for new license plates made as part of the vehicle registration renewal process shall not be considered an exchange of license plates for purposes of this section.</p><p>The provisions of this section shall apply to a replacement request made under the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-607/" target="_blank" rel="noreferrer">46.2-607</a> for license plates that are not duplicates or otherwise equivalent to the lost, mutilated, or illegible plates required to be replaced under that section. Such a request shall be considered both a replacement for purposes of §§ <a href="http://law.lis.virginia.gov/vacode/46.2-607/" target="_blank" rel="noreferrer">46.2-607</a> and <a href="http://law.lis.virginia.gov/vacode/46.2-692/" target="_blank" rel="noreferrer">46.2-692</a> and an exchange for purposes of this section.</p><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0057" target="_blank" rel="noreferrer">57</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0070" target="_blank" rel="noreferrer">70</a>.</p>',6),n=[s];function i(l,c,h,p,f,d){return a(),t("div",null,n)}const _=e(o,[["render",i]]);export{u as __pageData,_ as default};
