import{_ as e,o as i,c as n,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-706.1 Insurance and surety companies to furnish certain insurance information.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-706.1.md","filePath":"vacode/46.2-706.1.md"}'),r={name:"vacode/46.2-706.1.md"},t=a('<h1 id="§-46-2-706-1-insurance-and-surety-companies-to-furnish-certain-insurance-information" tabindex="-1">§ 46.2-706.1 Insurance and surety companies to furnish certain insurance information. <a class="header-anchor" href="#§-46-2-706-1-insurance-and-surety-companies-to-furnish-certain-insurance-information" aria-label="Permalink to &quot;§ 46.2-706.1 Insurance and surety companies to furnish certain insurance information.&quot;">​</a></h1><p>A. Any liability insurance information relating to individually identified vehicles or persons, received from such companies under this section, shall be considered privileged information and not subject to the Virginia Freedom of Information Act (§ <a href="/vacode/2.2-3700/">2.2-3700</a> et seq.).</p><p>B. Such information shall be used in conjunction with information supplied under § <a href="/vacode/46.2-706/">46.2-706</a> to verify insurance for motor vehicles certified by their owners to be insured.</p><p>C. Insurance companies licensed to do business in Virginia shall provide to the Department, electronically in a manner prescribed by the Commissioner, updates within 30 days of a policy change to liability insurance for a vehicle registered in Virginia, including liability insurance that satisfies financial responsibility requirements. A policy change occurs when an insurance company (i) issues liability insurance, (ii) cancels liability insurance, (iii) becomes aware of a lapse in liability insurance, (iv) reissues or reinstates liability insurance, or (v) adds a vehicle to an existing liability insurance policy.</p><p>D. Insurance companies licensed to do business in Virginia shall respond electronically in a manner prescribed by the Commissioner to a Department request for acknowledgment of liability insurance within 15 days of receiving the request. Insurance companies shall respond to the request by confirming or denying the existence of a policy with the company.</p><p>E. Every update of a policy change concerning a liability insurance policy shall include the following information: vehicle identification number, full name of first named insured, vehicle make, and vehicle model year. If available, the following information shall also be included: date of birth for first named insured, full names and dates of birth for all vehicle operators, and Virginia drivers&#39; license numbers or social security numbers for the first named insured and all vehicle operators.</p><p>1993, c. 949; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0474" target="_blank" rel="noreferrer">474</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0419" target="_blank" rel="noreferrer">419</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0149" target="_blank" rel="noreferrer">149</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0193" target="_blank" rel="noreferrer">193</a>.</p>',7),o=[t];function s(c,l,u,d,f,h){return i(),n("div",null,o)}const g=e(r,[["render",s]]);export{m as __pageData,g as default};
