import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-715 Display of license plates.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-715.md","filePath":"vacode/46.2-715.md"}'),r={name:"vacode/46.2-715.md"},s=o('<h1 id="§-46-2-715-display-of-license-plates" tabindex="-1">§ 46.2-715 Display of license plates. <a class="header-anchor" href="#§-46-2-715-display-of-license-plates" aria-label="Permalink to &quot;§ 46.2-715 Display of license plates.&quot;">​</a></h1><p>License plates assigned to a motor vehicle, other than a moped, motorcycle, autocycle, tractor truck, trailer, or semitrailer, or to persons licensed as motor vehicle dealers or transporters of unladen vehicles, shall be attached to the front and the rear of the vehicle. The license plate assigned to a moped, motorcycle, autocycle, trailer, or semitrailer shall be attached to the rear of the vehicle. The license plate assigned to a tractor truck shall be attached to the front of the vehicle. The license plates issued to licensed motor vehicle dealers and to persons licensed as transporters of unladen vehicles shall consist of one plate for each set issued and shall be attached to the rear of the vehicle to which it is assigned.</p><p>For any summons issued for a violation of this section, the court may, in its discretion, dismiss the summons, where proof of compliance with this section is provided to the court on or before the court date.</p><p>Code 1950, § 46-101; 1954, c. 210; 1958, c. 541, § 46.1-106; 1972, c. 609; 1974, c. 150; 1989, c. 727; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>.</p>',4),i=[s];function l(c,n,h,d,p,f){return t(),a("div",null,i)}const g=e(r,[["render",l]]);export{m as __pageData,g as default};
