import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-716 How license plates fastened to vehicle; altering appearance of license plates.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-716.md","filePath":"vacode/46.2-716.md"}'),o={name:"vacode/46.2-716.md"},r=i('<h1 id="§-46-2-716-how-license-plates-fastened-to-vehicle-altering-appearance-of-license-plates" tabindex="-1">§ 46.2-716 How license plates fastened to vehicle; altering appearance of license plates. <a class="header-anchor" href="#§-46-2-716-how-license-plates-fastened-to-vehicle-altering-appearance-of-license-plates" aria-label="Permalink to &quot;§ 46.2-716 How license plates fastened to vehicle; altering appearance of license plates.&quot;">​</a></h1><p>A. Every license plate shall be securely fastened to the motor vehicle, trailer, or semitrailer to which it is assigned:</p><ol><li><p>So as to prevent the plate from swinging,</p></li><li><p>In a position to be clearly visible, and</p></li><li><p>In a condition to be clearly legible.</p></li></ol><p>B. No colored glass, colored plastic, bracket, holder, mounting, frame, or any other type of covering shall be placed, mounted, or installed on, around, or over any license plate if such glass, plastic, bracket, holder, mounting, frame, or other type of covering in any way alters or obscures (i) the alpha-numeric information, (ii) the color of the license plate, (iii) the name or abbreviated name of the state wherein the vehicle is registered, or (iv) any character or characters, decal, stamp, or other device indicating the month or year in which the vehicle&#39;s registration expires. No insignia, emblems, or trailer hitches or couplings shall be mounted in such a way as to hide or obscure any portion of the license plate or render any portion of the license plate illegible.</p><p>C. The Superintendent may make such regulations as he may deem advisable to enforce the proper mounting and securing of the license plate on the vehicle.</p><p>D. For any summons issued for a violation of this section, the court may, in its discretion, dismiss the summons, where proof of compliance with this section is provided to the court on or before the court date.</p><p>Code 1950, § 46-102; 1958, c. 541, § 46.1-107; 1960, c. 119; 1986, c. 186; 1989, c. 727; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0258" target="_blank" rel="noreferrer">258</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0019" target="_blank" rel="noreferrer">19</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0549" target="_blank" rel="noreferrer">549</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>.</p>',7),n=[r];function l(s,c,p,h,d,f){return a(),t("div",null,n)}const u=e(o,[["render",l]]);export{m as __pageData,u as default};
