import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-720 Use of license plates from another vehicle in certain circumstances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-720.md","filePath":"vacode/46.2-720.md"}'),r={name:"vacode/46.2-720.md"},i=a('<h1 id="§-46-2-720-use-of-license-plates-from-another-vehicle-in-certain-circumstances" tabindex="-1">§ 46.2-720 Use of license plates from another vehicle in certain circumstances. <a class="header-anchor" href="#§-46-2-720-use-of-license-plates-from-another-vehicle-in-certain-circumstances" aria-label="Permalink to &quot;§ 46.2-720 Use of license plates from another vehicle in certain circumstances.&quot;">​</a></h1><p>The owner of a motor vehicle to which license plates have been assigned by the Department may remove the license plates from the motor vehicle and use them on another motor vehicle owned by a person operating a garage or owned by a motor vehicle dealer provided such use does not extend for more than five days and provided the use is limited to the time during which the first motor vehicle is being repaired or while the second motor vehicle is loaned to him for demonstration, as provided by § <a href="http://law.lis.virginia.gov/vacode/46.2-719/" target="_blank" rel="noreferrer">46.2-719</a>.</p><p>For the purposes of this section, the term &quot;motor vehicle&quot; does not include a &quot;moped&quot; as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-100/" target="_blank" rel="noreferrer">46.2-100</a>.</p><p>1960, c. 457, § 46.1-110.1; 1989, c. 727; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>.</p>',4),n=[i];function s(c,l,h,d,m,p){return t(),o("div",null,n)}const v=e(r,[["render",s]]);export{_ as __pageData,v as default};
