import{_ as e,o as i,c as a,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-721 Application of liability insurance policy to vehicle carrying plates from insured vehicle.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-721.md","filePath":"vacode/46.2-721.md"}'),t={name:"vacode/46.2-721.md"},r=o('<h1 id="§-46-2-721-application-of-liability-insurance-policy-to-vehicle-carrying-plates-from-insured-vehicle" tabindex="-1">§ 46.2-721 Application of liability insurance policy to vehicle carrying plates from insured vehicle. <a class="header-anchor" href="#§-46-2-721-application-of-liability-insurance-policy-to-vehicle-carrying-plates-from-insured-vehicle" aria-label="Permalink to &quot;§ 46.2-721 Application of liability insurance policy to vehicle carrying plates from insured vehicle.&quot;">​</a></h1><p>The policy of liability insurance issued to the owner of a motor vehicle and covering the operation thereof shall extend to and be the primary insurance applicable to his operation of a motor vehicle on which he has placed license tags from another motor vehicle as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-720/" target="_blank" rel="noreferrer">46.2-720</a>.</p><p>For the purposes of this section, the term &quot;motor vehicle&quot; does not include a &quot;moped&quot; as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-100/" target="_blank" rel="noreferrer">46.2-100</a>.</p><p>1960, c. 457, § 46.1-110.2; 1989, c. 727; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>.</p>',4),n=[r];function l(c,s,p,h,d,f){return i(),a("div",null,n)}const u=e(t,[["render",l]]);export{v as __pageData,u as default};
