import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-731 Disabled parking license plates; owners of vehicles specially equipped and used to transport persons with disabilities; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-731.md","filePath":"vacode/46.2-731.md"}'),r={name:"vacode/46.2-731.md"},s=t('<h1 id="§-46-2-731-disabled-parking-license-plates-owners-of-vehicles-specially-equipped-and-used-to-transport-persons-with-disabilities-fees" tabindex="-1">§ 46.2-731 Disabled parking license plates; owners of vehicles specially equipped and used to transport persons with disabilities; fees. <a class="header-anchor" href="#§-46-2-731-disabled-parking-license-plates-owners-of-vehicles-specially-equipped-and-used-to-transport-persons-with-disabilities-fees" aria-label="Permalink to &quot;§ 46.2-731 Disabled parking license plates; owners of vehicles specially equipped and used to transport persons with disabilities; fees.&quot;">​</a></h1><p>On receipt of an application, the Commissioner shall issue appropriately designed disabled parking license plates to persons with physical disabilities that limit or impair their ability to walk or that create a concern for his safety while walking or to the parents or legal guardians of such persons. The Commissioner shall request that the application be accompanied by a certification signed by a licensed physician, licensed podiatrist, licensed chiropractor, licensed nurse practitioner, or licensed physician assistant that the applicant meets the definition of &quot;person with a disability that limits or impairs his ability to walk&quot; contained in § <a href="http://law.lis.virginia.gov/vacode/46.2-1240/" target="_blank" rel="noreferrer">46.2-1240</a>. The issuance of a disabled parking license plate shall not preclude the issuance of a permanent removable windshield placard.</p><p>On application of an organization, the Commissioner shall issue disabled parking license plates for vehicles registered in the applicant&#39;s name if the vehicles are primarily used to transport persons with disabilities. The application shall include a certification by the applicant, under criteria determined by the Commissioner, that the vehicle is primarily used to transport persons with disabilities that limit or impair their ability to walk, as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-1240/" target="_blank" rel="noreferrer">46.2-1240</a>.</p><p>The fee for the issuance of a disabled parking license plate under this section may not exceed the fee charged for a similar license plate for the same class vehicle.</p><p>1972, c. 473, § 46.1-104.1; 1973, c. 182; 1974, cc. 46, 410; 1976, cc. 410, 460; 1978, cc. 185, 605; 1982, c. 88; 1983, c. 38; 1986, c. 144; 1989, c. 727; 1993, c. 566; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0225" target="_blank" rel="noreferrer">225</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0866" target="_blank" rel="noreferrer">866</a>; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0776" target="_blank" rel="noreferrer">776</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0904" target="_blank" rel="noreferrer">904</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0692" target="_blank" rel="noreferrer">692</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0715" target="_blank" rel="noreferrer">715</a>.</p>',5),l=[s];function n(o,p,c,h,d,f){return i(),a("div",null,l)}const u=e(r,[["render",n]]);export{b as __pageData,u as default};
