import{_ as e,o as a,c as s,O as n}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-732 Special license plates and decals for the deaf; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-732.md","filePath":"vacode/46.2-732.md"}'),t={name:"vacode/46.2-732.md"},o=n('<h1 id="§-46-2-732-special-license-plates-and-decals-for-the-deaf-fees" tabindex="-1">§ 46.2-732 Special license plates and decals for the deaf; fees. <a class="header-anchor" href="#§-46-2-732-special-license-plates-and-decals-for-the-deaf-fees" aria-label="Permalink to &quot;§ 46.2-732 Special license plates and decals for the deaf; fees.&quot;">​</a></h1><p>On receipt of an application, the Commissioner shall issue appropriately designed license plates to deaf persons. For purposes of this section, a deaf person shall be defined as a person who cannot hear and understand normal speech. The fee for these license plates shall be as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-694/" target="_blank" rel="noreferrer">46.2-694</a>.</p><p>The Commissioner shall also issue to any deaf person a removable decal, to be used on any passenger car, pickup or panel truck operated by such person. The decals shall be of a design determined by the Commissioner and shall be displayed in a manner determined by the Superintendent of State Police. A reasonable fee to be determined by the Commissioner shall be charged each person issued a decal under this section, but no fee shall be charged any person exempted from fees by § <a href="http://law.lis.virginia.gov/vacode/46.2-739/" target="_blank" rel="noreferrer">46.2-739</a>.</p><p>It shall be unlawful for any person who is not a person described in this section to willfully and falsely represent himself as having the qualifications to obtain the special plates or decal.</p><p>The provisions of subdivisions 1 and 2 of subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-725/" target="_blank" rel="noreferrer">46.2-725</a> shall not apply to license plates issued under this section.</p><p>1979, c. 74, § 46.1-104.2; 1989, c. 727; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',6),r=[o];function i(l,d,c,p,h,f){return a(),s("div",null,r)}const u=e(t,[["render",i]]);export{b as __pageData,u as default};
