import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-735 Special license plates for members of volunteer emergency medical services agencies and members of volunteer emergency medical services agency auxiliaries; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-735.md","filePath":"vacode/46.2-735.md"}'),s={name:"vacode/46.2-735.md"},c=i('<h1 id="§-46-2-735-special-license-plates-for-members-of-volunteer-emergency-medical-services-agencies-and-members-of-volunteer-emergency-medical-services-agency-auxiliaries-fees" tabindex="-1">§ 46.2-735 Special license plates for members of volunteer emergency medical services agencies and members of volunteer emergency medical services agency auxiliaries; fees. <a class="header-anchor" href="#§-46-2-735-special-license-plates-for-members-of-volunteer-emergency-medical-services-agencies-and-members-of-volunteer-emergency-medical-services-agency-auxiliaries-fees" aria-label="Permalink to &quot;§ 46.2-735 Special license plates for members of volunteer emergency medical services agencies and members of volunteer emergency medical services agency auxiliaries; fees.&quot;">​</a></h1><p>The Commissioner, on application, shall supply members of volunteer emergency medical services agencies and members of volunteer emergency medical services agency auxiliaries special license plates bearing the letters &quot;R S&quot; followed by numbers or letters or any combination thereof.</p><p>Only one application shall be required from each volunteer emergency medical services agency or volunteer emergency medical services agency auxiliary. The application shall contain the names and residence addresses of all members of the volunteer emergency medical services agency and members of the volunteer emergency medical services agency auxiliary who request license plates. Each volunteer emergency medical services agency or volunteer emergency medical services agency auxiliary shall notify the Commissioner within 30 days of separation of any member from such agency or agency auxiliary.</p><p>The Commissioner shall charge the prescribed cost of state license plates for each set of license plates issued under this section.</p><p>1972, c. 605, § 46.1-105.3; 1978, c. 201; 1987, c. 696; 1989, c. 727; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1026" target="_blank" rel="noreferrer">1026</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0635" target="_blank" rel="noreferrer">635</a>.</p>',5),n=[c];function l(o,t,m,g,f,d){return r(),a("div",null,n)}const u=e(s,[["render",l]]);export{v as __pageData,u as default};
