import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-738 Special license plates for amateur radio operators.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-738.md","filePath":"vacode/46.2-738.md"}'),o={name:"vacode/46.2-738.md"},i=t('<h1 id="§-46-2-738-special-license-plates-for-amateur-radio-operators" tabindex="-1">§ 46.2-738 Special license plates for amateur radio operators. <a class="header-anchor" href="#§-46-2-738-special-license-plates-for-amateur-radio-operators" aria-label="Permalink to &quot;§ 46.2-738 Special license plates for amateur radio operators.&quot;">​</a></h1><p>The Commissioner, on request, may supply any amateur radio operator licensed by the federal government or an agency thereof with license plates bearing his official call letters.</p><p>If more than one request is made for use, as provided in this section, of license plates having the same alpha-numeric, the Department shall accept the first such application. Persons receiving amateur radio operator special license plates shall affix such plates only to vehicles to which they are the titled owner.</p><p>The Commissioner shall charge a fee of one dollar in addition to the prescribed cost of state license plates for each set of license plates issued under the provisions of this section.</p><p>Code 1950, § 46-22.1; 1952, c. 675; 1954, c. 630; 1958, c. 541, § 46.1-105; 1987, c. 696; 1989, c. 727; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0943" target="_blank" rel="noreferrer">943</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0994" target="_blank" rel="noreferrer">994</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0331" target="_blank" rel="noreferrer">331</a>.</p>',5),s=[i];function l(n,c,p,h,d,f){return a(),r("div",null,s)}const u=e(o,[["render",l]]);export{m as __pageData,u as default};
