import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-741 Special license plates for survivors of attack on Pearl Harbor; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-741.md","filePath":"vacode/46.2-741.md"}'),i={name:"vacode/46.2-741.md"},o=r('<h1 id="§-46-2-741-special-license-plates-for-survivors-of-attack-on-pearl-harbor-fees" tabindex="-1">§ 46.2-741 Special license plates for survivors of attack on Pearl Harbor; fees. <a class="header-anchor" href="#§-46-2-741-special-license-plates-for-survivors-of-attack-on-pearl-harbor-fees" aria-label="Permalink to &quot;§ 46.2-741 Special license plates for survivors of attack on Pearl Harbor; fees.&quot;">​</a></h1><p>On receipt of an application and written evidence that the applicant is an honorably discharged former member of one of the armed forces of the United States and, while serving in the armed forces of the United States, was present during the attack on the island of Oahu, Territory of Hawaii, on December 7, 1941, between the hours of 7:55 a.m. and 9:45 a.m., Hawaii time, the Commissioner shall issue to the applicant special license plates identifying the vehicle as registered to a Pearl Harbor survivor.</p><p>For each set of license plates issued under this section, the Commissioner shall charge, in addition to the prescribed cost of state license plates, a one-time fee of ten dollars at the time the plates are issued.</p><p>The provisions of subdivisions 1 and 2 of subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-725/" target="_blank" rel="noreferrer">46.2-725</a> shall not apply to license plates issued under this section.</p><p>1987, c. 467, § 46.1-105.16; 1989, c. 727; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>.</p>',5),s=[o];function n(l,c,p,f,h,d){return a(),t("div",null,s)}const u=e(i,[["render",n]]);export{v as __pageData,u as default};
