import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-742.1 Special license plates for persons awarded the Bronze Star, Bronze Star with a \\"V\\" for valor, or the Silver Star; fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-742.1.md","filePath":"vacode/46.2-742.1.md"}'),s={name:"vacode/46.2-742.1.md"},i=t('<h1 id="§-46-2-742-1-special-license-plates-for-persons-awarded-the-bronze-star-bronze-star-with-a-v-for-valor-or-the-silver-star-fee" tabindex="-1">§ 46.2-742.1 Special license plates for persons awarded the Bronze Star, Bronze Star with a &quot;V&quot; for valor, or the Silver Star; fee. <a class="header-anchor" href="#§-46-2-742-1-special-license-plates-for-persons-awarded-the-bronze-star-bronze-star-with-a-v-for-valor-or-the-silver-star-fee" aria-label="Permalink to &quot;§ 46.2-742.1 Special license plates for persons awarded the Bronze Star, Bronze Star with a &quot;V&quot; for valor, or the Silver Star; fee.&quot;">​</a></h1><p>On receipt of an application and written evidence that the applicant has been awarded a Bronze Star, Bronze Star with a &quot;V&quot; for valor, or Silver Star Medal, the Commissioner shall issue to the applicant special license plates.</p><p>For each set of license plates issued under this section, other than those that reflect the Bronze Star, the Commissioner shall charge the prescribed cost of state license plates. For Bronze Star license plates, the Commissioner shall charge, in addition to the prescribed cost of state license plates, a one-time fee of $10 at the time the plates are issued.</p><p>The provisions of subdivisions B 1 and 2 of § <a href="/vacode/46.2-725/">46.2-725</a> shall not apply to license plates issued under this section.</p><p>The design of license plates issued under this section to persons who have been awarded multiple decorations shall reflect the number of such decorations.</p><p>Unremarried surviving spouses of persons eligible to receive special license plates under this section may also be issued special license plates under this section.</p><p>1992, c. 577; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1026" target="_blank" rel="noreferrer">1026</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0907" target="_blank" rel="noreferrer">907</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0864" target="_blank" rel="noreferrer">864</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0145" target="_blank" rel="noreferrer">145</a>.</p>',7),o=[i];function n(l,c,p,h,d,f){return r(),a("div",null,o)}const u=e(s,[["render",n]]);export{g as __pageData,u as default};
