import{_ as e,o as s,c as i,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-742.2 Special license plates for persons awarded the Navy Cross, the Distinguished Service Cross, the Air Force Cross, the Distinguished Flying Cross, or the Distinguished Flying Cross with a \\"V\\" for Valor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-742.2.md","filePath":"vacode/46.2-742.2.md"}'),t={name:"vacode/46.2-742.2.md"},a=r('<h1 id="§-46-2-742-2-special-license-plates-for-persons-awarded-the-navy-cross-the-distinguished-service-cross-the-air-force-cross-the-distinguished-flying-cross-or-the-distinguished-flying-cross-with-a-v-for-valor" tabindex="-1">§ 46.2-742.2 Special license plates for persons awarded the Navy Cross, the Distinguished Service Cross, the Air Force Cross, the Distinguished Flying Cross, or the Distinguished Flying Cross with a &quot;V&quot; for Valor. <a class="header-anchor" href="#§-46-2-742-2-special-license-plates-for-persons-awarded-the-navy-cross-the-distinguished-service-cross-the-air-force-cross-the-distinguished-flying-cross-or-the-distinguished-flying-cross-with-a-v-for-valor" aria-label="Permalink to &quot;§ 46.2-742.2 Special license plates for persons awarded the Navy Cross, the Distinguished Service Cross, the Air Force Cross, the Distinguished Flying Cross, or the Distinguished Flying Cross with a &quot;V&quot; for Valor.&quot;">​</a></h1><p>On receipt of an application and written evidence that the applicant has been awarded the Navy Cross, the Distinguished Service Cross, the Air Force Cross, the Distinguished Flying Cross, or the Distinguished Flying Cross with a &quot;V&quot; for Valor, the Commissioner shall issue to the applicant special license plates.</p><p>The provisions of subdivisions B 1 and 2 of § <a href="/vacode/46.2-725/">46.2-725</a> shall not apply to license plates issued under this section.</p><p>Unremarried surviving spouses of persons eligible to receive special license plates under this section may also be issued special license plates under this section.</p><p>The design of license plates issued under this section to persons who have been awarded multiple decorations shall reflect the number of such decorations.</p><p>For each set of license plates issued under this section, other than those that reflect the Distinguished Flying Cross, the Commissioner shall charge the prescribed cost of state license plates. For Distinguished Flying Cross license plates, the Commissioner shall charge, in addition to the prescribed cost of state license plates, a one-time fee of $10 at the time the plates are issued.</p><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0228" target="_blank" rel="noreferrer">228</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0301" target="_blank" rel="noreferrer">301</a>; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1026" target="_blank" rel="noreferrer">1026</a>; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0145" target="_blank" rel="noreferrer">145</a>.</p>',7),o=[a];function n(l,h,c,p,d,g){return s(),i("div",null,o)}const v=e(t,[["render",n]]);export{u as __pageData,v as default};
