import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-742 Special license plates for persons awarded Purple Heart; fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-742.md","filePath":"vacode/46.2-742.md"}'),t={name:"vacode/46.2-742.md"},s=i('<h1 id="§-46-2-742-special-license-plates-for-persons-awarded-purple-heart-fee" tabindex="-1">§ 46.2-742 Special license plates for persons awarded Purple Heart; fee. <a class="header-anchor" href="#§-46-2-742-special-license-plates-for-persons-awarded-purple-heart-fee" aria-label="Permalink to &quot;§ 46.2-742 Special license plates for persons awarded Purple Heart; fee.&quot;">​</a></h1><p>On receipt of an application and written evidence that the applicant has been awarded the Purple Heart, the Commissioner shall issue to the applicant special license plates.</p><p>No fee shall be charged for license plates issued under this section to any one motor vehicle owned and used personally by any applicant. For each additional set of license plates issued to an applicant under this section, the Commissioner shall charge the prescribed fee for state license plates.</p><p>Unremarried surviving spouses of persons eligible to receive special license plates under this section may also be issued special license plates under this section.</p><p>The provisions of subdivisions 1 and 2 of subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-725/" target="_blank" rel="noreferrer">46.2-725</a> shall not apply to license plates issued under this section.</p><p>The design of license plates issued under this section to persons who have been awarded multiple decorations shall reflect the number of such decorations.</p><p>1987, cc. 466, 472, § 46.1-105.15; 1989, c. 727; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0922" target="_blank" rel="noreferrer">922</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1026" target="_blank" rel="noreferrer">1026</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0614" target="_blank" rel="noreferrer">614</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0436" target="_blank" rel="noreferrer">436</a>.</p>',7),l=[s];function n(o,p,c,d,h,f){return a(),r("div",null,l)}const _=e(t,[["render",n]]);export{u as __pageData,_ as default};
