import{_ as e,o as a,c as s,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-744 Special license plates for members of National Guard; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-744.md","filePath":"vacode/46.2-744.md"}'),r={name:"vacode/46.2-744.md"},o=t('<h1 id="§-46-2-744-special-license-plates-for-members-of-national-guard-fees" tabindex="-1">§ 46.2-744 Special license plates for members of National Guard; fees. <a class="header-anchor" href="#§-46-2-744-special-license-plates-for-members-of-national-guard-fees" aria-label="Permalink to &quot;§ 46.2-744 Special license plates for members of National Guard; fees.&quot;">​</a></h1><p>On receipt of an application and written confirmation that the applicant is a member of the National Guard, the Commissioner shall issue to the applicant special license plates.</p><p>No fee shall be charged for license plates issued under this section to a member of the Virginia National Guard for any one motor vehicle owned and used personally by the applicant, unless the plates bear reserved numbers or letters as provided for in § <a href="/vacode/46.2-726/">46.2-726</a>. In this latter case, the fee for the issuance of license plates shall be the same as for those issued under § <a href="/vacode/46.2-726/">46.2-726</a>. For each additional set of license plates issued to an applicant under this section, the Commissioner shall charge the prescribed fee for state license plates.</p><p>The fee for members of non-Virginia National Guard units shall be ten dollars per year plus the prescribed cost for state license plates, unless the plates bear reserved numbers or letters as provided for in § <a href="/vacode/46.2-726/">46.2-726</a>. In this latter case, such license plates shall be subject to an additional charge of ten dollars per year for the reserved numbers or letters.</p><p>The provisions of subdivisions 1 and 2 of subsection B of § <a href="/vacode/46.2-725/">46.2-725</a> shall not apply to license plates issued under this section.</p><p>1981, c. 355, § 46.1-105.9; 1982, c. 85; 1989, c. 727; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0252" target="_blank" rel="noreferrer">252</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0153" target="_blank" rel="noreferrer">153</a>.</p>',6),i=[o];function l(n,c,p,f,d,h){return a(),s("div",null,i)}const _=e(r,[["render",l]]);export{m as __pageData,_ as default};
