import{_ as e,o as a,c as s,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-745.3 Special license plates for persons awarded the Air Medal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-745.3.md","filePath":"vacode/46.2-745.3.md"}'),i={name:"vacode/46.2-745.3.md"},o=t('<h1 id="§-46-2-745-3-special-license-plates-for-persons-awarded-the-air-medal" tabindex="-1">§ 46.2-745.3 Special license plates for persons awarded the Air Medal. <a class="header-anchor" href="#§-46-2-745-3-special-license-plates-for-persons-awarded-the-air-medal" aria-label="Permalink to &quot;§ 46.2-745.3 Special license plates for persons awarded the Air Medal.&quot;">​</a></h1><p>On receipt of an application and written confirmation from one of the United States Armed Forces that the applicant has been awarded the United States Air Medal, the Commissioner shall issue special license plates for persons awarded the Air Medal to such person.</p><p>For each set of plates issued under this section, the Commissioner shall charge a $10 annual special license plate fee in addition to the prescribed cost of state license plates.</p><p>It shall be unlawful for any person who is not a person described in this section to willfully and falsely represent himself as having the qualifications to obtain the special license plates herein provided for.</p><p>The provisions of subdivisions B 1 and 2 of § <a href="/vacode/46.2-725/">46.2-725</a> shall not apply to license plates issued under this section.</p><p>Unremarried surviving spouses of persons eligible to receive special license plates under this section may also be issued special license plates under this section upon application.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0970" target="_blank" rel="noreferrer">970</a>.</p>',7),n=[o];function r(l,p,c,d,h,f){return a(),s("div",null,n)}const m=e(i,[["render",r]]);export{u as __pageData,m as default};
