import{_ as e,o as i,c as s,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-745.4 Special license plates for persons awarded the Distinguished Service Medal, the Navy Distinguished Service Medal, the Marine Corps Distinguished Service Medal, or the Air Force Distinguished Service Medal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-745.4.md","filePath":"vacode/46.2-745.4.md"}'),t={name:"vacode/46.2-745.4.md"},r=a('<h1 id="§-46-2-745-4-special-license-plates-for-persons-awarded-the-distinguished-service-medal-the-navy-distinguished-service-medal-the-marine-corps-distinguished-service-medal-or-the-air-force-distinguished-service-medal" tabindex="-1">§ 46.2-745.4 Special license plates for persons awarded the Distinguished Service Medal, the Navy Distinguished Service Medal, the Marine Corps Distinguished Service Medal, or the Air Force Distinguished Service Medal. <a class="header-anchor" href="#§-46-2-745-4-special-license-plates-for-persons-awarded-the-distinguished-service-medal-the-navy-distinguished-service-medal-the-marine-corps-distinguished-service-medal-or-the-air-force-distinguished-service-medal" aria-label="Permalink to &quot;§ 46.2-745.4 Special license plates for persons awarded the Distinguished Service Medal, the Navy Distinguished Service Medal, the Marine Corps Distinguished Service Medal, or the Air Force Distinguished Service Medal.&quot;">​</a></h1><p>On receipt of an application and written confirmation from one of the armed services that the applicant has been awarded the Distinguished Service Medal, the Navy Distinguished Service Medal, the Marine Corps Distinguished Service Medal, or the Air Force Distinguished Service Medal, the Commissioner shall issue special license plates to such persons.</p><p>For each set of plates issued under this section, the Commissioner shall charge, in addition to the prescribed cost of state license plates, a one-time fee of $10 at the time the plates are issued.</p><p>It shall be unlawful for any person who is not a person described in this section to willfully and falsely represent himself as having the qualifications to obtain the special license plates herein provided for.</p><p>The provisions of subdivisions B 1 and 2 of § <a href="/vacode/46.2-725/">46.2-725</a> shall not apply to license plates issued under this section.</p><p>Unremarried surviving spouses of persons eligible to receive special license plates under this section may also be issued special license plates under this section.</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0145" target="_blank" rel="noreferrer">145</a>.</p>',7),n=[r];function d(o,l,c,h,p,u){return i(),s("div",null,n)}const g=e(t,[["render",d]]);export{f as __pageData,g as default};
